// Barracuda-shaped fish stick (realistic style, medium complexity)
// Overall dimensions: 80mm length, 20mm width, 12mm height

$fn = 32;

// Main body - elongated fish shape
module fish_body() {
    color([0.9, 0.7, 0.5])  // Golden brown fried color

    union() {
        // Main body segment
        translate([0, 0, 6])
            scale([1, 0.3, 0.6])
            sphere(d = 60);

        // Tail segment
        translate([-35, 0, 6])
            scale([1.2, 0.2, 0.4])
            sphere(d = 40);

        // Head segment
        translate([30, 0, 6])
            scale([0.8, 0.35, 0.7])
            sphere(d = 50);
    }
}

// Fish tail
module fish_tail() {
    color([0.85, 0.65, 0.45])

    translate([-45, 0, 6])
        rotate([0, 0, 0])
        linear_extrude(height = 2)
        polygon(points=[
            [0, 0],
            [-15, -10],
            [-15, 10],
            [0, 0]
        ]);
}

// Dorsal fin
module dorsal_fin() {
    color([0.8, 0.6, 0.4])

    for(i = [20:-5:-20]) {
        translate([i, 0, 11])
            rotate([0, 90, 0])
            scale([0.3, 1, 1])
            cylinder(h = 8, r1 = 3, r2 = 0.5);
    }
}

// Pectoral fins
module pectoral_fins() {
    color([0.8, 0.6, 0.4])

    // Left fin
    translate([10, 10, 5])
        rotate([0, 0, -30])
        scale([1, 0.3, 0.1])
        sphere(d = 15);

    // Right fin
    mirror([0, 1, 0])
        translate([10, 10, 5])
            rotate([0, 0, -30])
            scale([1, 0.3, 0.1])
            sphere(d = 15);
}

// Breading texture pattern
module breading_texture() {
    color([0.9, 0.8, 0.6])

    // Random breading bumps
    for(i = [0:10:80]) {
        for(j = [-8:4:8]) {
            for(k = [3:2:9]) {
                if((i + j + k) % 7 == 0) {
                    translate([i - 40, j, k])
                        sphere(d = 1 + (i % 3));
                }
            }
        }
    }
}

// Eyes
module eyes() {
    color([0, 0, 0])

    // Left eye
    translate([35, 5, 8])
        sphere(d = 2);

    // Right eye
    translate([35, -5, 8])
        sphere(d = 2);
}

// Mouth line
module mouth() {
    color([0.6, 0.4, 0.3])

    translate([40, 0, 5])
        rotate([90, 0, 0])
        linear_extrude(height = 1)
        square([8, 2]);
}

// Scale texture lines
module scale_lines() {
    color([0.7, 0.5, 0.3])

    for(i = [-20:5:25]) {
        hull() {
            translate([i, 0, 6.1])
                sphere(d = 0.5);
            translate([i + 2, 0, 6.1])
                sphere(d = 0.5);
        }
    }
}

// Assembly
union() {
    fish_body();
    fish_tail();
    dorsal_fin();
    pectoral_fins();
    breading_texture();
    eyes();
    mouth();
    scale_lines();
}

// Venison steak with bone - realistic style

// Main parameters
steak_width = 60;
steak_length = 80;
steak_thickness = 15;
bone_length = 40;
bone_diameter = 8;

// Render the complete venison steak
color([0.4, 0.2, 0.15]) {
    union() {
        // Main steak body
        translate([0, 0, steak_thickness/2]) {
            difference() {
                // Base steak shape
                scale([1.2, 1, 1]) 
                sphere(d=steak_width);

                // Cut bottom flat
                translate([0, 0, -steak_thickness]) 
                cube([steak_width*2, steak_width*2, steak_thickness*2], center=true);

                // Bone socket
                translate([steak_width/2 - 10, 0, -steak_thickness]) 
                cylinder(h=steak_thickness*2, d=bone_diameter + 4, center=true);
            }
        }

        // Bone protruding from steak
        translate([steak_width/2 - 5, 0, steak_thickness/2]) {
            rotate([0, 90, 0]) {
                cylinder(h=bone_length, d=bone_diameter);

                // Bone end shape
                translate([bone_length, 0, 0]) 
                sphere(d=bone_diameter + 2);
            }
        }

        // Steak thickness variation
        translate([0, 0, steak_thickness/2 + 2]) {
            // Fat layer on edges
            color([0.9, 0.8, 0.7]) {
                for(i = [0:30:359]) {
                    rotate([0, 0, i]) 
                    translate([steak_width/2 - 5, 0, 0]) 
                    scale([1, 0.3, 0.3]) 
                    sphere(d=8);
                }
            }

            // Muscle fiber texture
            color([0.35, 0.18, 0.12]) {
                for(y = [-steak_length/2 + 10:5:steak_length/2 - 10]) {
                    for(x = [-steak_width/2 + 10:8:steak_width/2 - 10]) {
                        translate([x, y, 1]) 
                        rotate([0, 0, 45]) 
                        scale([1, 0.2, 0.1]) 
                        cube([6, 2, 1], center=true);
                    }
                }
            }
        }

        // Grill marks
        color([0.25, 0.12, 0.08]) {
            for(i = [-20:10:20]) {
                translate([i, 0, steak_thickness + 0.5]) 
                rotate([90, 0, 0]) 
                scale([0.3, 1, 1]) 
                cylinder(h=steak_length - 10, d=2);
            }
            for(j = [-25:10:25]) {
                translate([0, j, steak_thickness + 0.5]) 
                rotate([0, 90, 0]) 
                scale([1, 0.3, 1]) 
                cylinder(h=steak_width - 10, d=2);
            }
        }
    }
}

// Bison-shaped gingerbread cookie (medium complexity, realistic style)
// Overall size: 80mm x 55mm x 12mm

$fn = 32;

// Main body - gingerbread colored
color([0.6, 0.35, 0.15]) {
    // Body base - rounded rectangular shape
    translate([0, 0, 0]) {
        hull() {
            translate([-25, -15, 0]) sphere(12);
            translate([25, -15, 0]) sphere(12);
            translate([-25, 15, 0]) sphere(12);
            translate([25, 15, 0]) sphere(12);
        }
        linear_extrude(height=8) scale([0.8, 0.7]) offset(r=5) circle(r=20);
    }

    // Head
    translate([28, 0, 5]) {
        union() {
            // Main head shape
            scale([1.2, 0.8, 0.8]) sphere(10);

            // Muzzle
            translate([10, 0, -2]) {
                scale([1.3, 0.9, 0.6]) sphere(6);
            }

            // Horns
            for(side = [-1, 1]) {
                translate([5, side * 6, 8]) {
                    rotate([0, side * 15, 0]) {
                        cylinder(h=8, r1=2, r2=1);
                    }
                }
            }
        }
    }

    // Legs
    for(side = [-1, 1]) {
        for(pos = [-10, 10]) {
            translate([pos, side * 18, 0]) {
                cube([6, 4, 10], center=true);
            }
        }
    }

    // Hump
    translate([-15, 0, 8]) {
        scale([1, 1.2, 0.6]) sphere(12);
    }

    // Tail
    translate([-28, 12, 6]) {
        rotate([0, 0, 45]) {
            scale([0.3, 1, 0.3]) sphere(8);
        }
    }
}

// Gingerbread texture - sugar coating
color([0.9, 0.9, 0.9]) {
    // Beard texture
    for(i = [1:6]) {
        angle = i * 60;
        translate([35 + cos(angle) * 3, sin(angle) * 4, 4]) {
            sphere(0.5);
        }
    }

    // Body sugar spots
    for(i = [1:20]) {
        x = (rand(i) - 0.5) * 50;
        y = (rand(i*2) - 0.5) * 30;
        z = 8.5;
        translate([x, y, z]) {
            sphere(0.8);
        }
    }
}

// Eyes - chocolate drops
color([0.2, 0.1, 0.05]) {
    for(side = [-1, 1]) {
        translate([35, side * 4, 6]) {
            sphere(1.5);
        }
    }
}

// Hooves - darker gingerbread
color([0.4, 0.2, 0.1]) {
    for(side = [-1, 1]) {
        for(pos = [-10, 10]) {
            translate([pos, side * 18, -5]) {
                cube([7, 5, 3], center=true);
            }
        }
    }
}

// Icing details
color([0.95, 0.95, 0.85]) {
    // Outline along back
    for(t = [0:0.1:1]) {
        x = lerp(-25, 25, t);
        y = lerp(0, 0, t) + sin(t * 180) * 3;
        z = 8.5;
        translate([x, y, z]) {
            sphere(0.6);
        }
    }

    // Face outline
    for(t = [0:0.1:1]) {
        angle = t * 360;
        x = 28 + cos(angle) * 8;
        y = sin(angle) * 6;
        z = 5 + sin(angle * 2) * 2;
        translate([x, y, z]) {
            sphere(0.5);
        }
    }
}

// Helper functions
function lerp(a, b, t) = a + (b - a) * t;
function rand(x) = (sin(x * 123.456) + 1) / 2;

// Elk meat jerky strip - realistic style
// Overall dimensions: 80mm x 25mm x 8mm

$fn = 32;

// Main color for elk jerky - dark reddish brown
color([0.4, 0.2, 0.15]) {
    // Main meat body
    difference() {
        // Base shape - slightly curved strip
        translate([0, 0, 4]) {
            scale([1, 0.8, 0.8]) {
                hull() {
                    translate([-35, 0, 0]) sphere(10);
                    translate([35, 0, 0]) sphere(10);
                    translate([-30, -10, 0]) sphere(8);
                    translate([30, 10, 0]) sphere(8);
                }
            }
        }

        // Tapered ends
        translate([-45, 0, 4]) rotate([0, 0, 0]) cube([20, 30, 10], center=true);
        translate([45, 0, 4]) rotate([0, 0, 0]) cube([20, 30, 10], center=true);
    }

    // Surface texture - meat grain lines
    for(i = [-30:5:30]) {
        translate([i, -12, 8.1]) {
            rotate([90, 0, 0]) {
                scale([0.3, 0.5, 1]) {
                    cylinder(h = 24, r1 = 1, r2 = 0.5);
                }
            }
        }
    }

    // Fat marbling spots
    color([0.8, 0.7, 0.6]) {
        for(x = [-25:10:25]) {
            for(y = [-8:4:8]) {
                translate([x + (x/10), y, 7.5 + sin(x+y)*0.5]) {
                    scale([1, 0.7, 0.3]) {
                        sphere(1.5);
                    }
                }
            }
        }
    }

    // Surface imperfections
    color([0.3, 0.15, 0.1]) {
        for(i = [1:15]) {
            angle = i * 137.5;
            r = 15 + sin(i*2) * 8;
            translate([cos(angle) * r, sin(angle) * r * 0.6, 8.2]) {
                sphere(0.5 + sin(i)*0.3);
            }
        }
    }

    // Edges - slightly rough
    difference() {
        // Main edge shape
        translate([0, 0, 4]) {
            scale([1, 0.7, 0.8]) {
                hull() {
                    translate([-35, 0, 0]) sphere(11);
                    translate([35, 0, 0]) sphere(11);
                    translate([-30, -11, 0]) sphere(9);
                    translate([30, 11, 0]) sphere(9);
                }
            }
        }

        // Inner smooth shape
        translate([0, 0, 4]) {
            scale([0.9, 0.65, 0.7]) {
                hull() {
                    translate([-35, 0, 0]) sphere(10);
                    translate([35, 0, 0]) sphere(10);
                    translate([-30, -10, 0]) sphere(8);
                    translate([30, 10, 0]) sphere(8);
                }
            }
        }
    }

    // Bottom surface with drying marks
    translate([0, 0, 0.5]) {
        difference() {
            cube([80, 25, 1], center=true);

            // Grid pattern from drying rack
            for(x = [-35:5:35]) {
                translate([x, 0, -0.1]) cube([0.5, 26, 1.2]);
            }
            for(y = [-10:5:10]) {
                translate([0, y, -0.1]) cube([80, 0.5, 1.2]);
            }
        }
    }
}

