// Caribou cookie cutter (medium complexity, realistic food item)
// Overall dimensions: ~80mm long x 50mm tall x 8mm thick

// Basic parameters
$fn = 32;
cookie_thickness = 8;
cutter_wall = 1.5;
body_height = 40;
body_length = 65;

// Cookie base shape
module cookie_base() {
    difference() {
        // Main cookie body
        color("burlywood")
        scale([1, 0.7, 1])
        cylinder(h = cookie_thickness, r = body_length/2, center = true);

        // Add some irregular edges for realism
        for(i = [0:15]) {
            rotate([0, 0, i*360/16])
            translate([body_length/2 - 3, 0, cookie_thickness/2])
            sphere(r = 1.5 + sin(i*3)*0.5);
        }
    }
}

// Caribou silhouette cutter
module caribou_silhouette() {
    union() {
        // Main body
        translate([0, 0, cookie_thickness/2])
        scale([1, 0.8, 1])
        cylinder(h = cookie_thickness + 0.1, r = 18);

        // Neck
        translate([0, 15, cookie_thickness/2])
        rotate([0, 0, -10])
        scale([0.4, 1.2, 1])
        cylinder(h = cookie_thickness + 0.1, r = 8);

        // Head
        translate([-5, 25, cookie_thickness/2])
        scale([0.8, 1, 1])
        sphere(r = 12);

        // Antlers (left)
        translate([-8, 22, cookie_thickness/2 + 8])
        rotate([0, 0, -30])
        cylinder(h = 15, r = 1.5);
        translate([-10, 17, cookie_thickness/2 + 15])
        rotate([0, 0, -60])
        cylinder(h = 8, r = 1);

        // Antlers (right)
        translate([8, 22, cookie_thickness/2 + 8])
        rotate([0, 0, 30])
        cylinder(h = 15, r = 1.5);
        translate([10, 17, cookie_thickness/2 + 15])
    rotate([0, 0, 60])
    cylinder(h = 8, r = 1);

    // Legs
    translate([-10, -15, cookie_thickness/2])
    cylinder(h = 15, r = 2);
    translate([-4, -15, cookie_thickness/2])
    cylinder(h = 15, r = 2);
    translate([4, -15, cookie_thickness/2])
    cylinder(h = 15, r = 2);
    translate([10, -15, cookie_thickness/2])
    cylinder(h = 15, r = 2);
    }
}

// Cookie texture details
module cookie_texture() {
    difference() {
        cookie_base();

        // Caribou cutout
        caribou_silhouette();

        // Add texture dots
        for(i = [0:30]) {
            rotate([0, 0, i*360/30 + 15])
            translate([body_length/2 - 8, 0, cookie_thickness - 0.5])
            sphere(r = 0.8);
        }
    }
}

// Create the cookie
cookie_texture();

// Optional: Add some sprinkles on top for decoration
module sprinkles() {
    color("red")
    translate([5, 5, cookie_thickness])
    rotate([0, 90, 0])
    scale([0.1, 0.1, 2])
    cylinder(h = 1, r = 0.5);

    color("green")
    translate([-8, 10, cookie_thickness])
    rotate([90, 0, 0])
    scale([0.1, 0.1, 2])
    cylinder(h = 1, r = 0.5);

    color("white")
    translate([0, -5, cookie_thickness])
    rotate([45, 0, 45])
    scale([0.1, 0.1, 2])
    cylinder(h = 1, r = 0.5);
}

// Add a few decorative sprinkles
sprinkles();

