// Realistic 3D-printable rabbit carrot
// Overall dimensions ~70 mm

$fn = 36;               // Smooth curves
wall = 1.2;             // 3D-print friendly wall thickness

module carrot_body() {
    color([0.92,0.33,0.12])
    difference() {
        // Main tapered body
        union() {
            // Slight S-curve
            hull() {
                translate([0,0,-5]) sphere(d=12);
                translate([0,0,25]) sphere(d=18);
                translate([0,4,60]) sphere(d=10);
            }
            // Add bulge
            translate([0,0,30]) scale([1,1,1.4]) sphere(d=16);
        }

        // Hollow core
        translate([0,0,0])
            cylinder(d1=18-2*wall, d2=8-2*wall, h=70);

        // Texture rings
        for (i=[5:10:55]) {
            rotate([0,90,0])
                translate([0,0,-i])
                    difference() {
                        cylinder(d=18.2, h=1.5, center=true);
                        cylinder(d=16.2, h=2, center=true);
                    }
        }
    }
}

module carrot_top() {
    // Green stems
    color([0.18,0.55,0.22])
    for (ang=[0:120:240]) {
        rotate([ang,0,0]) {
            translate([0,0,64])
                rotate([0,ang/3,0])
                    scale([0.25,0.25,1])
                        cylinder(d1=6, d2=2, h=25);
        }
    }
