// Main rice cluster module
module rice_cluster() {
    // Base rice grain shape
    module rice_grain(length, width, height, rotation) {
        translate([0, 0, height/2])
        rotate([rotation, 0, 0])
        scale([width, width, length])
        difference() {
            // Main rice body - elongated ellipsoid
            sphere(d=1, $fn=16);

            // Flat bottom for 3D printing
            translate([0, 0, -0.5])
            cube([2, 2, 1], center=true);

            // Subtle texture lines along the grain
            for(i = [-0.3:0.15:0.3]) {
                rotate([90, 0, 0])
                translate([i, 0, 0])
                rotate([0, 90, 0])
                cylinder(h=0.8, r=0.02, $fn=8);
            }
        }
    }

    // Generate natural rice cluster
    for(i = [0:8]) {
        // Random positioning for natural look
        angle = i * 40 + ((i % 3) * 15);
        radius = 8 + (i % 5) * 2;
        x_pos = cos(angle) * radius;
        y_pos = sin(angle) * radius;

        // Random variations in grain size and orientation
        grain_length = 5 + (i % 3) * 0.5;
        grain_width = 1.8 + (i % 4) * 0.2;
        grain_height = 2 + (i % 3) * 0.3;
        rotation = -10 + (i % 20);

        translate([x_pos, y_pos, 0])
        rotate([0, 0, angle + 90])
        rice_grain(grain_length, grain_width, grain_height, rotation);
    }

    // Central larger grains
