// Single stalk with grains
module wheat_stalk() {
    // Root system
    color(stalk_color)
    translate([0, 0, -root_bend])
    rotate([90, 0, 0])
    cylinder(h = root_bend * 2, d1 = stalk_diameter * 1.5, d2 = stalk_diameter * 0.5, $fn=8);

    // Main stalk
    color(stalk_color)
    cylinder(h = stalk_height, d = stalk_diameter, $fn=8);

    // Sheath (leaves around base)
    color(sheath_color)
    translate([0, 0, sheath_height/2])
    cylinder(h = sheath_height, d = sheath_diameter, $fn=12);

    // Side leaves
    for(i = [1:4]) {
        rotate([0, 0, i * 90])
        translate([sheath_diameter/2, 0, sheath_height/3])
        rotate([0, 30, 0])
        scale([0.3, 1, 0.1])
        sphere(d = sheath_height * 1.5, $fn=8);
    }

    // Grains on top - arranged in a wheat head
    for(i = [0:grain_count-1]) {
        // Spiral arrangement
        angle = i * 137.5; // golden angle
        radius = (i * 0.5) * (stalk_diameter/2);
        height = stalk_height + (i * 0.5);

        translate([sin(angle) * radius, cos(angle) * radius, height])
        rotate([0, 0, angle])
        grain(grain_length, grain_base_diameter);
    }

    // Small secondary grains
    for(i = [0:grain_count/2-1]) {
        angle = i * 110;
        radius = (i * 0.3) * (stalk_diameter/2);
        height = stalk_height - 2 + (i * 0.3);

        translate([sin(angle) * radius, cos(angle) * radius, height])
        rotate([0, 0, angle])
        grain(grain_length * 0.7, grain_base_diameter * 0.7);
    }
}

// Create wheat bundle
module wheat_bundle() {
    // Main stalk
    wheat_stalk();

    // Secondary stalks around main
    for(i = [1:5]) {
        angle = i * 72 + 15;
        radius = 8;
        height_offset = -5;
        lean_angle = 15;

        translate([sin(angle) * radius, cos(angle) * radius, height_offset])
        rotate([sin(angle) * lean_angle, cos(angle) * lean_angle, 0])
        scale([0.8, 0.8, 0.9])
        wheat_stalk();
    }
}

