    // Germ spiral - the characteristic quinoa feature
    color(germ_color) {
        for(i=[0:30:360]) {
            rotate([0, 0, i])
            translate([grain_length/3 - i/120, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=germ_thickness, d=germ_width, $fn=8);
        }
    }

    // Small surface details for texture
    for(i=[0:45:360]) {
        for(j=[-1:1]) {
            rotate([0, 0, i])
            translate([j*grain_length/4, 0, grain_width/2 - 0.2])
                sphere(d=0.3, $fn=6);
