            }
        }

        // Add some grains on top for texture
        for(k = [0 : 15]) {
            translate([
                rand(pile_diameter/2 - 5), 
                rand(pile_diameter/2 - 5), 
                pile_height/2 + rand(5)
            ]) {
                rotate([rand(180), rand(180), rand(180)]) 
                grain(grain_size * 0.8);
            }
        }
    }

    // Hollow out the base for printing efficiency
    translate([0, 0, -wall_thickness]) 
    scale([1, 1, 0.5]) 
    sphere(d = pile_diameter - wall_thickness * 2);
}

// Individual grain module - irregular wheat grain shape
module grain(size) {
    difference() {
        // Main grain body - elongated ellipsoid
        scale([1.4, 1, 0.6]) 
        sphere(d = size);

        // Add characteristic groove along grain
        translate([-size/2, 0, 0]) 
        rotate([0, 90, 0]) 
        cylinder(h = size * 1.5, r1 = size/6, r2 = size/8);

        // Add small indentations for texture
        for(m = [0 : 30 : 180]) {
            rotate([m, 0, 0]) 
            translate([rand(size/3), 0, rand(size/4)]) 
            sphere(d = size/5);
        }
    }

    // Add subtle ridge texture
    for(n = [0 : 45 : 360]) {
        rotate([0, 0, n]) 
        translate([0, size/3, 0]) 
        cylinder(h = size * 0.8, r = size/12);
    }
}

// Helper function for random values
function rand(max) = (rands(-max/2, max/2, 1))[0];

// Farro grain cluster - realistic style, medium complexity
// A small pile of farro grains showing the characteristic elongated shape with ridges

module farro_grain(length, width, thickness, twist) {
    // Main grain body - elongated ellipsoid
    color("#8B7355")
    rotate([twist, 0, 0])
    scale([length, width, thickness])
    sphere(r=1, $fn=12);

    // Ridge details along the grain
    for(i = [-0.6:0.3:0.6]) {
        color("#7A6348")
        translate([i*length*0.5, 0, 0])
        rotate([twist, 0, 0])
        scale([length*0.1, width*0.9, thickness*0.8])
        sphere(r=1, $fn=8);
    }

    // Small tip at one end
    color("#6B5A47")
    translate([length*0.8, 0, 0])
    rotate([twist, 0, 0])
    scale([length*0.3, width*0.5, thickness*0.5])
    cylinder(h=length*0.2, r1=1, r2=0, $fn=6);
}

