// Realistic Buckwheat Food Model - Medium Complexity
// Features: groats, hull segments, natural variation, 3D printable

// Parameters
groat_size = 4;
groats_per_row = 7;
groats_per_col = 5;
groat_height = 2;
base_thickness = 1.5;
hull_thickness = 0.8;
total_length = 70;
total_width = 50;

// Color definitions
groats_color = [0.6, 0.4, 0.2];
hull_color = [0.35, 0.25, 0.12];

// Single buckwheat groat with triangular hull
module buckwheat_groat(x, y, z, rotation) {
    // Translate to position
    translate([x, y, z]) {
        rotate([0, 0, rotation]) {
            // Main groat body - slightly flattened ellipsoid
            color(groats_color)
            scale([1, 0.7, 0.5])
            sphere(d = groat_size, $fn = 20);

            // Hull segments - three triangular pieces
            color(hull_color) {
                // First segment
                rotate([0, 0, 0])
                translate([0, 0, groat_size/4])
                rotate([60, 0, 0])
                cylinder(h = hull_thickness, r1 = groat_size/2.5, r2 = 0.1, $fn = 3);

                // Second segment
                rotate([0, 0, 120])
                translate([0, 0, groat_size/4])
                rotate([60, 0, 0])
                cylinder(h = hull_thickness, r1 = groat_size/2.5, r2 = 0.1, $fn = 3);

                // Third segment
                rotate([0, 0, 240])
                translate([0, 0, groat_size/4])
                rotate([60, 0, 0])
                cylinder(h = hull_thickness, r1 = groat_size/2.5, r2 = 0.1, $fn = 3);
            }
        }
    }
}

// Create a cluster of groats with natural variation
module buckwheat_cluster() {
    union() {
        // Base platform for printing stability
        color([0.25, 0.15, 0.08])
        cube([total_length, total_width, base_thickness], center = false);

        // Generate groats in a natural pattern
        for (i = [0:groats_per_row-1]) {
            for (j = [0:groats_per_col-1]) {
                // Add random variation to position
                x_pos = i * (total_length / groats_per_row) + groat_size/2 + (i % 2) * 2;
                y_pos = j * (total_width / groats_per_col) + groat_size/2 + (j % 2) * 2;

                // Random rotation for natural appearance
                rotation = (i * 73 + j * 37) % 360;

                // Slight height variation
                z_pos = base_thickness + groat_height/2 + (sin(i*0.7) + cos(j*0.5)) * 0.5;

                // Create main groat
                buckwheat_groat(x_pos, y_pos, z_pos, rotation);

                // Add some scattered individual groats for realism
                if ((i + j) % 3 == 0) {
                    // Small secondary groat nearby
                    buckwheat_groat(
                        x_pos + 3 + (i % 2) * 2,
                        y_pos + 2 + (j % 2) * 2,
                        base_thickness + groat_height/3,
                        rotation + 45
                    );
                }
            }
        }

        // Add some hull fragments on the base
        for (k = [0:15]) {
            x_frag = (k * 4.7) % total_length;
            y_frag = (k * 6.3) % total_width;
            color(hull_color)
            translate([x_frag, y_frag, base_thickness + 0.3])
            rotate([0, 0, k * 23])
            scale([0.3, 0.3, 0.1])
            cylinder(h = 0.5, r = 1.5, $fn = 3);
        }
    }
}

// Render the buckwheat cluster
buckwheat_cluster();

