wall_thickness = 1.5;

// Color definitions
grain_color = "#D4A574";  // Light brown/beige
darker_grain = "#B8956A";  // Darker brown for variation

// Single teff grain - elongated oval shape
module teff_grain(size, rotation, tilt) {
    color(grain_color) {
        translate([0, 0, -size/2]) {
            rotate([tilt, 0, rotation]) {
                // Main grain body
                scale([0.6, 0.4, 1]) 
                    sphere(d=size, $fn=12);

                // Slight ridge along grain
                translate([0, 0, size*0.3]) {
                    scale([0.1, 0.2, 0.8])
                        sphere(d=size, $fn=8);
                }
            }
        }
    }
}

// Darker grain variant for variation
module dark_teff_grain(size, rotation, tilt) {
    color(darker_grain) {
        translate([0, 0, -size/2]) {
            rotate([tilt, 0, rotation]) {
                scale([0.65, 0.35, 1]) 
                    sphere(d=size, $fn=10);
            }
        }
    }
}

// Random seed generator for natural variation
function random(seed) = rands(0, 1, 1, seed)[0];

// Create cluster of teff grains
module teff_cluster() {
    // Center support structure
    difference() {
        sphere(d=cluster_size * 0.3, $fn=32);
        sphere(d=cluster_size * 0.3 - wall_thickness * 2, $fn=32);
    }

    // Generate grains in a dome shape
    for(i = [0:grain_count-1]) {
        angle1 = random(i * 2) * 360;
        angle2 = random(i * 3) * 180;
        radius = random(i * 5) * (cluster_size/2 - grain_diameter);
        height = random(i * 7) * (cluster_size/2 - grain_diameter) * 0.7;

        // Position grains in spherical distribution
        x = cos(angle1) * sin(angle2) * radius;
        y = sin(angle1) * sin(angle2) * radius;
        z = cos(angle2) * radius + height;

        translate([x, y, z]) {
            if(i % 4 == 0) {
                dark_teff_grain(grain_diameter * (0.8 + random(i*11)*0.4), random(i*13)*360, random(i*17)*30-15);
            } else {
                teff_grain(grain_diameter * (0.9 + random(i*19)*0.2), random(i*23)*360, random(i*29)*20-10);
            }
        }
    }

    // Add some loose grains at bottom for natural look
    for(j = [0:20]) {
        angle = random(j * 31) * 360;
        dist = random(j * 37) * (cluster_size/2 - grain_diameter);

        translate([cos(angle) * dist, sin(angle) * dist, -cluster_size/4]) {
            rotate([90, 0, random(j*41)*360]) {
                teff_grain(grain_diameter * 0.7, 0, 0);
            }
        }
    }
}

// Base platform to prevent rolling
