    color("#E8DCC0") {
        cylinder(h=2, r1=cluster_size/2 + 5, r2=cluster_size/2 + 3, $fn=64);
    }
}

// Main assembly
union() {
    base();
    translate([0, 0, 2]) {
        teff_cluster();
    }
}

