// Create the main slice shape with rounded edges
module bread_slice() {
    // Main body with rounded top
    difference() {
        // Base shape
        union() {
            // Main loaf slice
            translate([0, 0, slice_height/2])
                cube([slice_length, slice_width, slice_height], center=true);

            // Rounded top edge
            translate([0, 0, slice_height])
                scale([slice_length/2, slice_width/2, slice_height/2])
                    sphere();
        }

        // Hollow out bottom slightly for realistic slice thickness
        translate([0, 0, -1])
            cube([slice_length-4, slice_width-4, slice_height/2], center=true);
    }
}

// Caraway seeds on top
module caraway_seeds() {
    color("darkslategray")
    for (i = [0:35]) {
        x = (i * 7 - 40) % slice_length - slice_length/2;
        y = (i * 11 - 20) % slice_width - slice_width/2;
        z = slice_height + 0.5;

        // Skip seeds near edges
        if (abs(x) < slice_length/2 - 8 && abs(y) < slice_width/2 - 8) {
            translate([x, y, z])
                rotate([90, 0, rand(i)*360])
                    scale([1, 1, 0.4])
                        sphere(seed_radius);
        }
    }
}

// Sunflower seeds
module sunflower_seeds() {
    color("saddlebrown")
    for (i = [0:15]) {
        x = (i * 13 - 25) % slice_length - slice_length/2;
        y = (i * 7 - 15) % slice_width - slice_width/2;
        z = slice_height + 0.3;

        if (abs(x) < slice_length/2 - 5 && abs(y) < slice_width/2 - 5) {
            translate([x, y, z])
                rotate([rand(i+50)*30, 0, rand(i+25)*180])
                    scale([2, 1.5, 0.3])
                        cylinder(h=seed_height*1.5, r1=seed_radius*1.5, r2=seed_radius);
        }
    }
}

// Rye grains embedded in surface
