    color("saddlebrown")
    for (i = [0:25]) {
        x = (i * 9 - 30) % slice_length - slice_length/2;
        y = (i * 5 - 10) % slice_width - slice_width/2;
        z = slice_height - 1;

        if (abs(x) < slice_length/2 - 3 && abs(y) < slice_width/2 - 3) {
            translate([x, y, z])
                rotate([90, 0, rand(i+100)*360])
                    scale([1.5, 0.8, 0.8])
                        sphere(seed_radius*0.8);
        }
    }
}

// Crumb texture holes
