    color("dimgray")
    for (i = [0:20]) {
        x = (i * 17 - 35) % slice_length - slice_length/2;
        y = (i * 13 - 25) % slice_width - slice_width/2;
        z = slice_height/2 + rand(i+200)*slice_height/2 - slice_height/4;

        if (abs(x) < slice_length/2 - 4 && abs(y) < slice_width/2 - 4) {
