// Triticale grain model (realistic style, medium complexity)
// Triticale is a wheat-rye hybrid, so this combines wheat and rye grain characteristics

// Main parameters
grain_length = 70;
grain_width = 5;
grain_thickness = 4;
seed_count = 15;
hull_thickness = 0.5;

// Colors
hull_color = "#D2B48C";  // Tan/beige
seed_color = "#B8860B";  // Dark goldenrod
groove_color = "#8B7355";  // Darker brown for texture

module triticale_grain() {
    // Main grain body - elongated with slight taper
    hull_shape() {
        // Create tapered grain body
        linear_extrude(height=grain_length, twist=15) {
            offset(r=2) offset(r=-2) {
                polygon([
                    [0, -grain_width/2],
                    [grain_thickness/2, -grain_width/3],
                    [grain_thickness*0.8, 0],
                    [grain_thickness/2, grain_width/3],
                    [0, grain_width/2]
                ]);
            }
        }
    }
}

module hull_shape() {
    color(hull_color) {
        // Main hull body
        hull() {
            // Create organic rounded shape
            for(i = [0:3]) {
                rotate([0, 0, i*90]) 
                    translate([0, 0, grain_length/2])
                        sphere(d=grain_width*1.2, $fn=16);
            }
        }

        // Add longitudinal ridges characteristic of triticale
        difference() {
            hull() {
                for(i = [0:3]) {
                    rotate([0, 0, i*90]) 
                        translate([0, 0, grain_length/2])
                            sphere(d=grain_width*1.2, $fn=16);
                }
            }

            // Carve out grooves between ridges
            for(i = [1:3]) {
                rotate([0, 0, i*120])
                    translate([0, 0, grain_length/2])
                        cylinder(h=grain_length*1.1, d=grain_width*0.3, $fn=8);
            }
        }
    }
}

module seed_details() {
    color(seed_color) {
        // Add seed texture with small spheres
        for(i = [0:seed_count-1]) {
            rotate([0, 0, i*360/seed_count])
                translate([grain_width/3, 0, grain_length/seed_count*i])
                    sphere(d=grain_width/6, $fn=8);
        }

        // Add bristle (awn) at top
        translate([0, 0, grain_length])
            rotate([90, 0, 0])
                cylinder(h=grain_length/3, d1=0.5, d2=0.2, $fn=8);
    }
}

