// Couscous - realistic style, medium complexity
// A bowl of fluffy couscous with visible individual grains

// Basic dimensions (in mm)
bowl_d = 70;
bowl_h = 30;
grain_d = 1.2;
grain_h = 1.5;

// Main assembly
color("wheat") {
    union() {
        // Ceramic bowl
        color("ivory") {
            difference() {
                // Outer bowl
                translate([0, 0, 0])
                    cylinder(d = bowl_d, h = bowl_h, $fn = 64);

                // Inner cavity
                translate([0, 0, 2])
                    cylinder(d = bowl_d - 4, h = bowl_h - 2, $fn = 64);

                // Flatten bottom
                translate([0, 0, -1])
                    cube([bowl_d, bowl_d, 2], center = true);
            }
        }

        // Couscous grains layer
        translate([0, 0, 2]) {
            // Create realistic mound shape
            for (i = [0:360:15]) {
                rotate([0, 0, i]) {
                    for (r = [5:7:25]) {
                        translate([r * sin(i), r * cos(i), 0]) {
                            // Vary height based on distance from center
                            h = 15 - r * 0.4;
                            grains_on_circle(r, h);
                        }
                    }
                }
            }

            // Add extra grains in center for fuller look
            translate([0, 0, 0]) {
                for (j = [0:25]) {
                    angle = j * 137.5;
                    radius = sqrt(j) * 1.8;
                    translate([
                        radius * cos(angle),
                        radius * sin(angle),
                        10 + sin(j) * 2
                    ]) {
                        single_grain();
                    }
                }
            }
        }

        // Some scattered grains on top
        translate([0, 0, 17]) {
            for (k = [0:45]) {
                translate([
                    (rand(k * 2) - 0.5) * 20,
                    (rand(k * 3) - 0.5) * 20,
                    rand(k * 5) * 3
                ]) {
                    single_grain();
                }
            }
        }
    }
}

// Module for a single couscous grain
module single_grain() {
    // Random rotation for natural look
    rotate([rand($children * 10) * 360, rand($children * 20) * 360, rand($children * 30) * 360]) {
        // Main grain body - slightly flattened sphere
        scale([1, 1, 0.8]) {
            sphere(d = grain_d, $fn = 8);
        }

        // Tiny bump for texture
        translate([0, 0, grain_d/2]) {
            scale([0.3, 0.3, 0.5]) {
                sphere(d = grain_d * 0.7, $fn = 6);
            }
        }
    }
}

// Module for placing grains in circular patterns
module grains_on_circle(radius, height) {
    count = max(3, floor(radius / 1.5));
    for (i = [0:count-1]) {
        angle = i * 360 / count;
        translate([
            radius * cos(angle),
            radius * sin(angle),
            height + rand(i) * 2 - 1
        ]) {
            single_grain();
        }
    }
}

// Simple pseudo-random function for variation
function rand(n) = (n * 9301 + 49297) % 233280 / 233280;

