caramel_color = [0.8, 0.5, 0.25];

// Create the semolina cake base
module semolina_base() {
    color(semolina_color)
    difference() {
        // Main cylinder body
        cylinder(h = height, r = plate_diameter/2 - 5, center = false);

        // Hollow center for realistic thickness
        translate([0, 0, wall_thickness])
        cylinder(h = height - wall_thickness, r = plate_diameter/2 - 5 - wall_thickness, center = false);
    }
}

// Create caramelized top surface texture
module caramelized_top() {
    color(caramel_color) {
        // Main caramel layer
        difference() {
            cylinder(h = 1.5, r = plate_diameter/2 - 5, center = false);
            // Slightly smaller for inset look
            translate([0, 0, -0.1])
            cylinder(h = 2, r = plate_diameter/2 - 5.5, center = false);
        }

        // Add caramelized spots and texture
        for(i = [0:15]) {
            angle = i * 24 + (i % 3) * 8;
            radius = (plate_diameter/2 - 8) * (0.7 + 0.3 * sin(i * 2));

            translate([
                radius * cos(angle),
                radius * sin(angle),
                1.4
            ])
            scale([1, 1, 0.3])
            sphere(r = 1.2 + 0.8 * (i % 3));
        }
    }
}

// Create roasted almonds on top
module almond() {
    color(almond_color)
    union() {
        // Main almond body
        scale([1, 0.6, 0.3])
        sphere(r = 3);

        // Almond texture lines
        for(i = [0:2]) {
            translate([0, 0, -0.2 + i * 0.2])
            scale([0.9, 0.5, 0.05])
            sphere(r = 3);
        }
    }
