    for(i = [0:11]) {
        angle = i * 30;
        radius = (plate_diameter/2 - 12) * (0.8 + 0.2 * (i % 2));

        translate([
            radius * cos(angle),
            radius * sin(angle),
            height + 0.8
        ])
        rotate([0, 0, angle + 45])
        rotate([20 + (i % 3) * 15, 0, 0])
        almond();
    }
}

// Add semolina grain texture on sides
module side_texture() {
    color(semolina_color)
    for(i = [0:35]) {
        angle = i * 10;
        height_pos = 3 + (i % 4) * 4;

        translate([
            (plate_diameter/2 - 5 - wall_thickness/2) * cos(angle),
            (plate_diameter/2 - 5 - wall_thickness/2) * sin(angle),
            height_pos
        ])
        rotate([90, 0, angle])
        scale([0.8, 0.8, 0.4])
        sphere(r = 0.8);
    }
}

