// Main breadfruit body with characteristic lobes
module breadfruit_body() {
    color([0.8, 0.65, 0.25])  // Yellow-green breadfruit color

    // Create the main spherical base
    difference() {
        sphere(d = 70, $fn = 64);

        // Flatten bottom slightly for printing stability
        translate([0, 0, -40])
            cube([80, 80, 50], center = true);
    }

    // Add lobes using scaled spheres
    for (i = [0:11]) {
        rotate([0, 0, i * 30])
            translate([0, 32, 0])
                scale([1.1, 0.7, 0.9])
                    sphere(d = 20, $fn = 16);
    }
}

// Surface texture - hexagonal pattern
module breadfruit_texture() {
    color([0.75, 0.6, 0.2])

    // Create hexagonal indentations
    for (lat = [-60:15:60]) {
        for (lon = [0:30:330]) {
            rotate([lat, 0, lon])
                translate([0, 0, 33])
                    rotate([90, 0, 0])
                        linear_extrude(height = 1)
                            circle(d = 4, $fn = 6);
        }
    }
}

// Stem attachment point
module breadfruit_stem() {
    color([0.4, 0.3, 0.1])

    // Main stem base
    translate([0, 0, 35])
        rotate([0, 0, 0])
            cylinder(h = 8, d1 = 12, d2 = 8, $fn = 8);

    // Stem collar
    translate([0, 0, 32])
        cylinder(h = 3, d = 15, $fn = 8);
}

// Surface blemishes for realism
module breadfruit_blemishes() {
    color([0.6, 0.45, 0.15])

    // Random small spots
    for (i = [0:20]) {
        rotate([rands(-80, 80, 1)[0], 0, rands(0, 360, 1)[0]])
            translate([rands(25, 33, 1)[0], 0, rands(-5, 5, 1)[0]])
                sphere(d = rands(1.5, 3, 1)[0], $fn = 8);
    }
}

// Main assembly
union() {
    // Core structure
    breadfruit_body();

    // Surface details
    difference() {
        breadfruit_texture();
        // Cut hexagons into surface
        for (lat = [-60:15:60]) {
            for (lon = [0:30:330]) {
                rotate([lat, 0, lon])
                    translate([0, 0, 34.5])
                        rotate([90, 0, 0])
                            cylinder(h = 3, d = 3.5, $fn = 6);
            }
        }
    }

    // Add stem
    breadfruit_stem();

    // Add blemishes for realism
    breadfruit_blemishes();
}

// Arrowroot - Realistic style with medium complexity
// Features: segmented root structure, fibrous texture, and natural irregularities

// Main parameters
root_length = 75;
root_width = 25;
wall_thickness = 2;

module arrowroot_root() {
    color([0.95, 0.85, 0.75])  // Creamy white color

    // Main root body with slight taper
    difference() {
        hull() {
            translate([0, 0, root_length/2]) 
                scale([1.1, 1.1, 1]) 
                sphere(d=root_width, $fn=20);
            translate([0, 0, 0]) 
                sphere(d=root_width*0.8, $fn=20);
        }

        // Create segmented appearance
        for(i = [0:8]) {
            translate([0, 0, i*root_length/9])
                rotate([0, 0, i*20])
                difference() {
                    cylinder(h=root_length/10, d=root_width*1.2, $fn=12);
                    translate([0, 0, -1]) 
                        cylinder(h=root_length/9+2, d=root_width*0.7, $fn=12);
                }
        }
    }

    // Fibrous root hairs
    for(i = [0:30]) {
        rotate([0, 0, i*12])
        translate([root_width/2 * cos(i*3), root_width/2 * sin(i*3), root_length/2 - (i%4)*root_length/4])
        rotate([90, 0, 0])
        cylinder(h=2+random(0,3), d=0.5+random(0,1.5), $fn=6);
    }

    // Natural surface bumps and ridges
    for(i = [0:15]) {
        translate([random(-root_width/3, root_width/3), 
                  random(-root_width/3, root_width/3), 
                  random(0, root_length)])
        scale([random(0.8,1.2), random(0.8,1.2), random(0.5,1.5)])
        sphere(d=2+random(0,2), $fn=8);
    }

    // Growth rings
    color([0.9, 0.8, 0.7]) {
        for(i = [1:5]) {
            translate([0, 0, root_length/6 * i])
            rotate_extrude($fn=30)
            translate([root_width/2 - i, 0, 0])
            circle(d=1);
        }
    }
}

// Helper function for random values
function random(min, max) = min + (max - min) * (rands(0, 1, 1)[0]);

// Create the complete arrowroot
rotate([0, -30, 0])
arrowroot_root();

// Kudzu Leaf-wrapped Rice Cake (Korean Traditional Food)
// Realistic style, medium complexity

module kudzu_leaf() {
    // Main leaf body - kidney bean shape
    difference() {
        // Base leaf shape
        scale([1, 0.7, 0.05]) sphere(d=40);

        // Leaf stem cutout
        translate([0, -20, 0]) scale([0.3, 0.3, 2]) cylinder(h=10, d=10);

        // Vein indentations
        for(i=[-2:2]) {
            rotate([0, 0, i*15]) translate([0, 5*i, 0]) scale([0.05, 0.8, 2]) 
                cube([30, 3, 2], center=true);
        }
    }

    // Leaf stem
    color("darkgreen") translate([0, -20, 0]) rotate([90, 0, 0]) 
        cylinder(h=15, d1=3, d2=2);

    // Leaf texture - small bumps
    for(i=[-4:4]) {
        for(j=[-2:2]) {
            if(i*i + j*j < 10) {
                translate([i*4, j*4, 2.5]) sphere(d=0.8);
            }
        }
    }
}

module rice_cake() {
    // Main rice cake body
    color("ivory") translate([0, 0, -7]) scale([0.8, 0.6, 0.3]) 
        sphere(d=35);

    // Rice grain texture on surface
    for(i=[-3:3]) {
        for(j=[-3:3]) {
            if(i*i + j*j < 9) {
                translate([i*3, j*3, -5.5]) sphere(d=1.2);
            }
        }
    }

    // Filling layer visible at edge
    color("darkred") translate([0, 0, -6]) scale([0.75, 0.55, 0.1]) 
        sphere(d=35);
}

module kudzu_powder() {
    // Dusting of kudzu powder on top
    color("white") {
        for(i=[-5:5]) {
            for(j=[-5:5]) {
                if(i*i + j*j < 16) {
                    translate([i*2, j*2, 2.6]) sphere(d=0.5);
                }
            }
        }
    }
}

// Main assembly
union() {
    // Kudzu leaf wrapping
    color("palegreen") kudzu_leaf();

    // Rice cake inside
    translate([0, 0, 2]) rice_cake();

    // Powder dusting
    kudzu_powder();
}

// Realistic 3D printable sago pearls with translucent appearance
// Sago pearls are small, round balls with slight irregularities and a semi-transparent look

$fn = 32;  // Smooth spheres for better print quality

// Main module for the sago cluster
module sago_cluster(pearl_count = 12, base_size = 60) {
    // Pearl parameters
    pearl_radius = base_size / 12;
    cluster_radius = base_size / 2.5;

    // Create multiple pearls in a natural cluster
    for (i = [0:pearl_count-1]) {
        // Random position within cluster
        angle1 = 360 * i / pearl_count + rands(-15, 15, 1)[0];
        angle2 = 180 * i / pearl_count + rands(-30, 30, 1)[0];
        radius = cluster_radius * (0.7 + rands(0, 0.3, 1)[0]);

        translate([
            radius * cos(angle1) * sin(angle2),
            radius * sin(angle1) * sin(angle2),
            radius * cos(angle2)
        ]) {
            // Each pearl with slight size variation
            actual_radius = pearl_radius * (0.9 + rands(0, 0.2, 1)[0]);
            sago_pearl(actual_radius);
        }
    }
}

// Individual sago pearl with realistic features
module sago_pearl(radius) {
    // Base pearl body
    difference() {
        // Main sphere
        color([0.95, 0.92, 0.85, 0.7])  // Translucent off-white
        sphere(r = radius);

        // Small indentations for texture
        for (i = [0:5]) {
            rotate([rands(0, 360, 1)[0], rands(0, 360, 1)[0], 0])
            translate([radius * 0.8, 0, 0])
            scale([1, 0.7, 0.7])
            sphere(r = radius * 0.15);
        }
    }

    // Add subtle surface irregularities
    for (i = [0:8]) {
        rotate([rands(0, 360, 1)[0], rands(0, 360, 1)[0], 0])
        translate([radius * 0.9, 0, 0])
        color([0.9, 0.87, 0.8, 0.5])
        sphere(r = radius * 0.05);
    }
}

// Create a small puddle of cooking liquid
module cooking_liquid() {
    // Thin base for printing stability
    color([0.85, 0.82, 0.75, 0.4])  // Translucent milky liquid
    difference() {
        // Main puddle shape
        scale([1, 0.8, 0.1])
        sphere(r = 35);

        // Irregular edges
        for (i = [0:20]) {
            rotate([0, 0, i * 18])
            translate([20 + rands(-5, 5, 1)[0], 0, -1])
            cube([10, 5, 2]);
        }
    }
}

// Main assembly
translate([0, 0, 5]) {
    // Cooking liquid base
    cooking_liquid();

    // Sago pearls cluster
    translate([0, 0, 2]) {
        sago_cluster(pearl_count = 15, base_size = 70);
    }

    // Add a few loose pearls around
    for (i = [0:7]) {
        angle = 45 * i + rands(-10, 10, 1)[0];
        distance = 40 + rands(-5, 5, 1)[0];
        translate([
            distance * cos(angle),
            distance * sin(angle),
            1 + rands(0, 2, 1)[0]
        ]) {
            sago_pearl(radius = 3.5 + rands(-0.5, 0.5, 1)[0]);
        }
    }
}

