// Udon noodle soup bowl - realistic style
// Bowl: 80mm diameter x 45mm height
// Noodles: 3mm diameter x 150mm length (folded)

// Colors
bowl_color = [0.95, 0.85, 0.75];
noodle_color = [0.95, 0.85, 0.6];
broth_color = [0.85, 0.65, 0.4];
green_color = [0.2, 0.6, 0.3];
white_color = [0.95, 0.95, 0.95];

$fn = 32;

// Main module
module udon_bowl() {
    // Bowl base
    color(bowl_color)
    difference() {
        cylinder(h = 45, r1 = 40, r2 = 35);
        translate([0, 0, 2])
        cylinder(h = 43, r1 = 38, r2 = 33);
    }

    // Bowl rim
    translate([0, 0, 45])
    color(bowl_color)
    difference() {
        cylinder(h = 3, r1 = 40, r2 = 41);
        cylinder(h = 3, r1 = 35, r2 = 36);
    }

    // Broth
    translate([0, 0, 2])
    color(broth_color)
    cylinder(h = 40, r1 = 38, r2 = 33);

    // Udon noodles
    module udon_noodle(start, end, twist) {
        color(noodle_color) {
            hull() {
                translate(start)
                sphere(d = 3);
                translate(end)
                sphere(d = 3);
            }
            translate(start)
            sphere(d = 3);
            translate(end)
            sphere(d = 3);
        }
    }

    // Generate folded noodles
    for (i = [0:3]) {
        angle = i * 90;
        radius = 15 + i * 3;

        // Main noodle loops
        rotate([0, 0, angle])
        translate([radius * cos(45), radius * sin(45), 25])
        rotate([90, 0, 45])
        for (j = [0:5]) {
            y = j * 8 - 20;
            udon_noodle([0, y, 0], [5, y + 6, 0], j * 15);
        }

        // Cross noodles
        rotate([0, 0, angle + 45])
        translate([radius * 0.7, 0, 30])
        for (k = [0:3]) {
            x = k * 6 - 9;
            udon_noodle([x, -15, 0], [x + 3, 15, 0], 0);
        }
    }

    // Green onions (negi)
    module green_onion() {
        color(green_color)
        for (i = [0:8]) {
            rotate([0, 0, i * 45])
            translate([20 + i * 2, 0, 38])
            rotate([90, 0, 0])
            cylinder(h = 5, r1 = 0.5, r2 = 0.3);
        }
    }

    green_onion();

    // Fish cake (kamaboko)
    color(white_color)
    translate([0, -25, 38])
    rotate([90, 0, 0])
    linear_extrude(height = 1.5)
    union() {
        circle(r = 8);
        translate([-3, 0, 0])
        circle(r = 3);
        translate([3, 0, 0])
        circle(r = 3);
    }

    // Pink spiral on fish cake
    color([0.95, 0.7, 0.75])
    translate([0, -25, 39.6])
    rotate([90, 0, 0])
    linear_extrude(height = 0.3)
    for (t = [0:5:360]) {
        x = 3 * cos(t) * (1 + t/360);
        y = 3 * sin(t) * (1 + t/360);
        translate([x, y, 0])
        circle(r = 0.8);
    }

    // Broth surface texture
    color(broth_color)
    translate([0, 0, 42])
    for (i = [0:20]) {
        angle = i * 18;
        radius = 5 + (i % 3) * 8;
        translate([radius * cos(angle), radius * sin(angle), 0])
        sphere(d = 1.5);
    }
}

// Render the udon bowl
udon_bowl();

// Realistic soba noodles with chopsticks in a simple bowl
// Model is ~80mm wide, designed for 3D printing with minimal overhangs

// Colors
soba_color = [0.5, 0.35, 0.2];
bowl_color = [0.9, 0.9, 0.95];
chopstick_color = [0.8, 0.65, 0.4];

// Main assembly
union() {
    // Bowl
    color(bowl_color) bowl();

    // Noodles
    color(soba_color) noodles();

    // Chopsticks
    color(chopstick_color) chopsticks();
}

// Bowl module
module bowl() {
    difference() {
        // Outer bowl
        cylinder(h = 25, r1 = 40, r2 = 35, $fn = 64);

        // Hollow interior
        translate([0, 0, 5])
            cylinder(h = 22, r1 = 37, r2 = 32, $fn = 64);

        // Flat bottom
        translate([0, 0, -1])
            cylinder(h = 2, r1 = 25, r2 = 25, $fn = 32);
    }
}

// Individual noodle strand
module noodle_strand(length, angle, twist) {
    // Create twisted noodle with elliptical cross-section
    for(i = [0:2:length]) {
        hull() {
            translate([i * cos(angle), i * sin(angle), 5 + sin(i/5 + twist) * 2])
                scale([1.5, 0.8, 1])
                    sphere(r = 1.2, $fn = 12);
            translate([(i+2) * cos(angle), (i+2) * sin(angle), 5 + sin((i+2)/5 + twist) * 2])
                scale([1.5, 0.8, 1])
                    sphere(r = 1.2, $fn = 12);
        }
    }
}

// Noodles collection
module noodles() {
    // Generate multiple strands in various orientations
    for(i = [0:20:340]) {
        rotate([0, 0, i])
            translate([sin(i/3) * 15, cos(i/3) * 15, 0])
                noodle_strand(35 + (i % 15), i/10, i/5);
    }

    // Additional crossing strands for realistic tangle
    for(i = [10:30:190]) {
        rotate([0, 0, i + 90])
            translate([cos(i/2) * 10, sin(i/2) * 10, 3])
                noodle_strand(25 + (i % 10), -i/8, -i/7);
    }
}

// Chopsticks crossed on top
module chopsticks() {
    // First chopstick
    rotate([90, 0, 45])
        translate([0, -35, 15])
            cylinder(h = 70, r1 = 1.5, r2 = 1.2, $fn = 16);

    // Second chopstick
    rotate([90, 0, -45])
        translate([0, -35, 15])
            cylinder(h = 70, r1 = 1.5, r2 = 1.2, $fn = 16);
}

// Realistic 3D printable macaroni food model
// Elbow macaroni with ridged surface texture and slight imperfections

$fn=64; // Smooth curves
macaroni_color = "#F5DEB3"; // Wheat pasta color

// Main module to create macaroni
module macaroni() {
    color(macaroni_color) {
        // Create elbow tube shape
        difference() {
            union() {
                // Main curved tube body
                curved_tube();

                // Surface ridges
                ridge_texture();
            }

            // Hollow center
            hollow_center();
        }

        // Slight imperfections for realism
        surface_imperfections();
    }
}

// Curved tube body
module curved_tube() {
    // Sweep a circle along a quarter-circle path
    for(i=[0:5:90]) {
        rotate([0, 0, i]) 
        translate([0, -30*sin(i), 30*cos(i)]) 
        rotate([90-i, 0, 0]) 
        cylinder(h=5, r1=9, r2=9.5, center=true);
    }
}

// Create hollow center
module hollow_center() {
    // Inner tube following same curve
    for(i=[0:5:90]) {
        rotate([0, 0, i]) 
        translate([0, -25*sin(i), 30*cos(i)]) 
        rotate([90-i, 0, 0]) 
        cylinder(h=7, r1=5.5, r2=6, center=true);
    }
}

// Surface ridges along the curve
module ridge_texture() {
    for(i=[0:15:360]) {
        rotate([0, 0, i]) {
            // Create ridges at 15 degree intervals
            for(j=[0:5:90]) {
                rotate([0, 0, j]) 
                translate([0, -30*sin(j), 30*cos(j)]) 
                rotate([90-j, 0, 0]) 
                translate([9.5, 0, 0]) 
                cube([1, 5, 0.5], center=true);
            }
        }
    }
}

// Add small imperfections for realistic look
module surface_imperfections() {
    // Random small bumps
    for(i=[0:30:330]) {
        rotate([0, 0, i]) {
            for(j=[0:15:75]) {
                rotate([0, 0, j]) 
                translate([0, -30*sin(j), 30*cos(j)]) 
                rotate([90-j, 0, 0]) 
                translate([9.5+2*cos(i*j), 0, 2*sin(i*j)]) 
                sphere(r=0.3);
            }
        }
    }

    // Small edge irregularities
    for(i=[0:45:315]) {
        rotate([0, 0, i]) 
        translate([0, -30*sin(i), 30*cos(i)]) 
        rotate([90-i, 0, 0]) 
        translate([0, 4.5, 0]) 
        sphere(r=0.5);
    }
}

// Create a single macaroni piece
macaroni();

// Fusilli pasta - medium complexity realistic style
// Overall length ~80mm, printable without supports

$fn = 30;

// Pasta parameters
spiral_length = 80;
spiral_radius = 8;
turns = 3;
ridge_height = 1.5;
noodle_thickness = 4;
groove_width = 3;

// Create the main spiral body
module spiral_body() {
    // Generate points along the spiral
    points = [for (i = [0:360*turns]) 
        [spiral_radius * cos(i), 
         spiral_radius * sin(i), 
         i * (spiral_length / (360 * turns))]
    ];

    // Create tubes along the spiral path
    for (i = [0:len(points)-2]) {
        translate(points[i])
