strand_length = 80;     // Length of each pasta strand

// Generate the pasta nest
module pasta_nest() {
    // Create a base disk for the nest bottom
    color("#E8DCC0") 
    translate([0, 0, -0.5])
    cylinder(h=1, d=nest_diameter, $fn=64);

    // Generate curved pasta strands in a spiral pattern
    for (i = [0:5:360]) {
        rotate([0, 0, i])
        translate([sin(i/3)*8, cos(i/3)*8, 0])
        pasta_strand(i);
    }
}

// Individual pasta strand module
module pasta_strand(rotation) {
    // Create the flat ribbon shape
    color("#F5E7C1") {
        // Main pasta body
        hull() {
            translate([-strand_length/2, -pasta_width/2, 0])
            cube([strand_length, pasta_width, pasta_thickness]);

            // Add slight curve to the strand
            translate([-strand_length/2 + 5, -pasta_width/2 - 0.5, 0.3])
            cube([strand_length - 10, pasta_width + 1, pasta_thickness]);

            translate([-strand_length/2 + 10, -pasta_width/2 - 0.8, 0.6])
            cube([strand_length - 20, pasta_width + 1.6, pasta_thickness]);
        }

        // Add texture lines along the pasta
        for (j = [0:3:strand_length-10]) {
            translate([-strand_length/2 + j + 5, -pasta_width/2, -0.1])
            cube([0.5, pasta_width, pasta_thickness + 0.2]);
        }

        // Add cross-hatch texture
        for (k = [0:2:pasta_width-2]) {
            translate([-strand_length/2, -pasta_width/2 + k, -0.1])
            cube([strand_length, 0.3, pasta_thickness + 0.2]);
        }
    }

    // Slight rotation for natural look
    rotate([sin(rotation/20)*3, cos(rotation/20)*3, 0])
    translate([0, 0, sin(rotation/15)*2])
    children();
}

