    color("#FFF8DC") {
        for (i = [0:15]) {
            translate([
                sin(i*137.5)*nest_diameter/2*0.9,
                cos(i*137.5)*nest_diameter/2*0.9,
                nest_height + 0.5
            ])
            sphere(d=0.5 + sin(i*20)*0.3);
        }
    }
}

