// Generate random spaghetti positions
module spaghetti_strand(length, angle, x_pos, y_pos, z_pos) {
    color($spaghetti_color) {
        // Main cylinder body
        translate([x_pos, y_pos, z_pos]) {
            rotate([0, angle, 0]) {
                cylinder(h=length, d=$strand_diameter, $fn=12);

                // Add texture rings every 5mm
                for(i = [0:5:length-5]) {
                    translate([0, 0, i]) {
                        difference() {
                            cylinder(h=0.5, d=$strand_diameter+0.2, $fn=12);
                            cylinder(h=0.5, d=$strand_diameter-0.2, $fn=12);
                        }
                    }
                }
            }
        }
    }
}

// Create tomato sauce blobs
module sauce_blob(x, y, size) {
    color($sauce_color) {
        translate([x, y, $strand_diameter + $plate_thickness + $sauce_thickness/2]) {
            sphere(d=size, $fn=16);
        }
    }
