    translate([0, 0, $plate_thickness/2]) base_plate();

    // Generate spaghetti strands in a pile
    for(i = [0:12]) {
        // Random positions within plate bounds
        x_pos = -30 + (i * 5) % 60 - 30;
        y_pos = -20 + (i * 3) % 40 - 20;

        // Random slight angles for natural look
        angle = (i % 2 == 0) ? 5 + (i*2) : -5 - (i*2);

        // Slight height variations
