    // Add tomato sauce on top
    for(i = [0:8]) {
        sauce_x = -25 + (i*5) % 50;
        sauce_y = -15 + (i*3) % 30;
        sauce_size = 4 + (i%3)*2;
        sauce_blob(sauce_x, sauce_y, sauce_size);
    }

    // Additional sauce drips between strands
    for(i = [0:5]) {
        translate([-20 + i*8, -10 + (i%2)*20, $plate_thickness + $strand_diameter]) {
            color($sauce_color) {
                cylinder(h=$sauce_thickness, d=6 + (i%2)*2, $fn=12);
            }
        }
    }
}

