module vermicelli_strand(length, curl, segments) {
    // Create a single strand with slight curl and texture
    for(i = [0:segments-1]) {
        // Calculate position along strand
        t = i / (segments - 1);
        angle = t * curl;
        x = sin(angle) * 2;
        y = cos(angle) * 2;
        z = t * length;

        // Create segment with slight random offset for texture
        translate([x + (rand(i*3)*0.3-0.15), 
                   y + (rand(i*7)*0.3-0.15), 
                   z]) {
