seed_count = 45;

// Base naan body with organic teardrop shape
module base_naan() {
    // Main body - elongated teardrop
    difference() {
        hull() {
            // Front rounded section
            translate([naan_length/2 - 15, 0, 0])
                scale([1.2, 1, 0.5])
                sphere(d=naan_width, $fn=32);

            // Back tapered section
            translate([-naan_length/2 + 10, 0, 0])
                scale([0.8, 0.9, 0.4])
                sphere(d=naan_width * 0.7, $fn=32);
        }

        // Hollow out for 3D printing
        translate([0, 0, -1])
            scale([0.85, 0.85, 0.5])
            hull() {
                translate([naan_length/2 - 15, 0, 0])
                    scale([1.2, 1, 0.5])
                    sphere(d=naan_width, $fn=32);
                translate([-naan_length/2 + 10, 0, 0])
                    scale([0.8, 0.9, 0.4])
                    sphere(d=naan_width * 0.7, $fn=32);
            }
    }
}

// Charred spots for realistic texture
module char_spots() {
    for(i = [0:20]) {
        random_x = rands(-naan_length/2 + 10, naan_length/2 - 10, 1)[0];
        random_y = rands(-naan_width/2 + 10, naan_width/2 - 10, 1)[0];
        random_size = rands(2, 5, 1)[0];

        translate([random_x, random_y, naan_thickness - 1])
            color([0.1, 0.1, 0.1])
            cylinder(h=char_thickness, d=random_size, $fn=12);
    }
}

// Nigella seeds (kalonji) - typical naan topping
module seeds() {
    for(i = [0:seed_count]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = sqrt(i) * 3; // Spiral pattern

        x_pos = cos(angle) * radius * 0.8;
        y_pos = sin(angle) * radius * 0.7;

        // Keep seeds within naan bounds
        if(abs(x_pos) < naan_length/2 - 5 && abs(y_pos) < naan_width/2 - 5) {
            translate([x_pos, y_pos, naan_thickness])
                color([0.05, 0.05, 0.05])
                scale([0.8, 0.8, 0.4])
                sphere(d=1.5, $fn=8);
        }
    }
}

// Air bubbles and dimples for realistic texture
module air_bubbles() {
    for(i = [0:30]) {
        bubble_x = rands(-naan_length/2 + 5, naan_length/2 - 5, 1)[0];
        bubble_y = rands(-naan_width/2 + 5, naan_width/2 - 5, 1)[0];
        bubble_size = rands(1, 3, 1)[0];

        translate([bubble_x, bubble_y, naan_thickness - 0.5])
            color([0.4, 0.25, 0.15])
            difference() {
                sphere(d=bubble_size * 2, $fn=16);
                translate([0, 0, -bubble_size])
                    cube([bubble_size * 4, bubble_size * 4, bubble_size * 2], center=true);
            }
    }
}

// Edge details - slightly burnt and irregular
module edge_browning() {
    hull() {
        for(i = [0:35]) {
            angle = i * 10.28;
            edge_x = cos(angle) * (naan_length/2 + 2);
            edge_y = sin(angle) * (naan_width/2 + 2);

            translate([edge_x * 0.9, edge_y * 0.7, naan_thickness/2])
                color([0.3, 0.15, 0.08])
                sphere(d=2 + sin(i*3)*0.5, $fn=8);
        }
    }
}

