        translate([0, 0, -0.5])
        scale([1, 1, 0.2])
        sphere(d = chapati_diameter*0.9, $fn = 100);
    }
}

// Random char spots for realistic appearance
module char_spots() {
    for(i = [1:char_spots]) {
        // Random positions using golden ratio for natural distribution
        angle = i * 137.508;
        radius = pow(i/char_spots, 0.7) * chapati_diameter * 0.45;

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            chapati_thickness - texture_depth
        ]) {
            // Dark char spots
            color([0.3, 0.2, 0.15])
            scale([1, 1, 0.1])
            sphere(d = 2 + (i % 3), $fn = 20);
