    for(angle = [0:30:359]) {
        rotate([0, 0, angle]) {
            for(r = [5:7:chapati_diameter/2 - 5]) {
                translate([r, 0, chapati_thickness - texture_depth/2])
                scale([1, 0.3, 0.1])
                sphere(d = 0.5 + r % 1.5, $fn = 8);
            }
        }
