    // Random small bubbles
    for(i = [1:50]) {
        angle = i * 73.156;
        radius = (i % 7) * chapati_diameter * 0.07;

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            chapati_thickness - texture_depth/2 - (i % 3) * 0.1
        ]) {
            color([0.8, 0.6, 0.4])
            sphere(d = 0.3 + (i % 4) * 0.2, $fn = 6);
        }
    }
}

// Edge detail - slightly thicker and irregular
