// Injera - Ethiopian sourdough flatbread with realistic teff texture
// Designed for 3D printing with 1.2mm wall thickness

// Main parameters
injera_diameter = 80;           // 80mm diameter
injera_thickness = 3;           // 3mm thick for printing stability
edge_thickness = 1.2;          // Thin edge for realistic profile
pore_size = 1.5;               // Size of fermentation bubbles
pore_density = 0.7;            // Density of bubbles (0-1)

// Color definitions
injera_color = [0.6, 0.4, 0.25];  // Light brown teff color

// Main injera base with slight dome shape
module injera_base() {
    color(injera_color) {
        // Create dome-shaped base using scaled sphere section
        difference() {
            scale([1, 1, 0.3])
                sphere(d = injera_diameter, $fn = 100);

            // Flatten bottom
            translate([0, 0, -injera_thickness/2])
                cube([injera_diameter*2, injera_diameter*2, injera_thickness], center = true);
        }
    }
}

// Create realistic teff grain texture pattern
module teff_texture() {
    // Random seed for consistent texture
    seed = 42;

    // Generate random pores across surface
    for(i = [0:200]) {
        // Random position on injera surface
        angle = rands(0, 360, 1, seed + i)[0];
        radius = rands(0, injera_diameter/2 - 5, 1, seed + i + 100)[0];
        x = cos(angle) * radius;
        y = sin(angle) * radius;

        // Vary pore size slightly
        current_pore = rands(pore_size*0.7, pore_size*1.3, 1, seed + i + 200)[0];

        // Only place pores with certain density
        if(rands(0, 1, 1, seed + i + 300)[0] < pore_density) {
            translate([x, y, injera_thickness/2 + 0.5])
                sphere(d = current_pore, $fn = 8);
        }
    }
}

// Create characteristic injera edge with slight lip
module injera_edge() {
    color(injera_color) {
        difference() {
            // Outer edge ring
            cylinder(h = edge_thickness*2, d = injera_diameter, $fn = 100);

            // Inner cutout for thin edge
            cylinder(h = edge_thickness*2 + 0.1, d = injera_diameter - 4, $fn = 100);
        }

        // Slight lip on top edge
        translate([0, 0, edge_thickness*2])
            cylinder(h = 0.5, d1 = injera_diameter, d2 = injera_diameter - 1, $fn = 100);
    }
}

// Main assembly
union() {
    // Base injera
    injera_base();

    // Edge detail
    injera_edge();

    // Surface texture (subtractive)
    color(injera_color) {
        difference() {
            translate([0, 0, injera_thickness/2 + 0.1])
                cylinder(h = 1, d = injera_diameter - 4, $fn = 100);

            // Apply teff texture as indentations
            translate([0, 0, -0.2])
                teff_texture();
        }
    }

    // Add some larger fermentation bubbles
    color(injera_color) {
        for(i = [0:15]) {
            angle = rands(0, 360, 1, seed + i + 400)[0];
            radius = rands(injera_diameter/4, injera_diameter/2 - 8, 1, seed + i + 500)[0];
            x = cos(angle) * radius;
            y = sin(angle) * radius;

            translate([x, y, injera_thickness/2])
                sphere(d = rands(2.5, 4, 1, seed + i + 600)[0], $fn = 12);
        }
    }
}

// Realistic 3D printable matzo
// Size ~80x70x3 mm
// Medium complexity

// Main parameters
matzo_width = 80;
matzo_height = 70;
matzo_thickness = 3;

// Surface texture parameters
bubble_size = 1.5;
bubble_depth = 0.4;
bubble_rows = 12;
bubble_cols = 8;

// Crack parameters
crack_width = 0.6;
crack_depth = 1.5;

// Color definitions
matzo_color = [235/255, 215/255, 180/255];  // Light brown/beige

// Main matzo body
module matzo_body() {
    difference() {
        // Main flat cracker
        color(matzo_color)
        cube([matzo_width, matzo_height, matzo_thickness], center = true);

        // Rounded edges
        translate([0, 0, -matzo_thickness/2 - 0.1])
        cylinder(h = matzo_thickness + 0.2, r1 = matzo_width/2 - 2, r2 = matzo_width/2 - 2, $fn = 64);

        // Edge rounding along width
        for(side = [-1, 1]) {
            translate([0, side * matzo_height/2, 0])
            rotate([90, 0, 0])
            cylinder(h = matzo_width - 4, r = 2, $fn = 32);

            translate([side * matzo_width/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h = matzo_height - 4, r = 2, $fn = 32);
        }
    }
}

// Surface bubbles (characteristic of matzo)
module surface_bubbles() {
    for(i = [0:bubble_rows - 1]) {
        for(j = [0:bubble_cols - 1]) {
            x_pos = -matzo_width/2 + 5 + i * (matzo_width - 10) / (bubble_rows - 1);
            y_pos = -matzo_height/2 + 5 + j * (matzo_height - 10) / (bubble_cols - 1);

            // Random offset to avoid perfect grid
            offset = (i * 3 + j * 7) % 3 - 1.5;

            translate([x_pos + offset, y_pos + offset/2, matzo_thickness/2 - bubble_depth/2])
            sphere(d = bubble_size, $fn = 16);
        }
    }
}

// Crack pattern
module crack_pattern() {
    // Main diagonal crack
    difference() {
        cube([matzo_width, matzo_height, matzo_thickness], center = true);

        color([0.8, 0.7, 0.6])
        translate([0, 0, -matzo_thickness/2 - 0.1])
        rotate([0, 0, 35])
        cube([matzo_width * 1.5, crack_width, matzo_thickness + 0.2], center = true);
    }

    // Branching cracks
    for(i = [0:3]) {
        angle = 35 + (i - 1.5) * 20;
        length = matzo_width * 0.4;

        difference() {
            cube([matzo_width, matzo_height, matzo_thickness], center = true);

            translate([5 - i * 10, 5, -matzo_thickness/2 - 0.1])
            rotate([0, 0, angle])
            cube([length, crack_width, matzo_thickness + 0.2], center = true);
        }
    }
}

// Edge imperfections
module edge_imperfections() {
    for(i = [0:5]) {
        angle = i * 60;
        x = cos(angle) * (matzo_width/2 - 2);
        y = sin(angle) * (matzo_height/2 - 2);

        translate([x, y, 0])
        rotate([0, 0, angle])
        difference() {
            cube([matzo_width, matzo_height, matzo_thickness], center = true);

            translate([0, 0, -matzo_thickness/2 - 0.1])
            rotate([0, 0, 45])
            cube([8, 2, matzo_thickness + 0.2], center = true);
        }
    }
}

// Assembly
union() {
    // Main matzo
    matzo_body();

    // Surface details
    difference() {
        matzo_body();

        // Indentations for bubbles
        translate([0, 0, -matzo_thickness/2 + bubble_depth/2])
        surface_bubbles();
    }

    // Crack pattern overlay
    crack_pattern();

    // Edge details
    edge_imperfections();
}

// Realistic Crouton Model
