// Create main crouton body with rounded edges
module crouton_body() {
    color(bread_color) {
        // Base rectangular shape with rounded edges
        hull() {
            // Bottom corners
            translate([edge_radius, edge_radius, 0]) 
                sphere(r=edge_radius, $fn=8);
            translate([crouton_length-edge_radius, edge_radius, 0]) 
                sphere(r=edge_radius, $fn=8);
            translate([edge_radius, crouton_width-edge_radius, 0]) 
                sphere(r=edge_radius, $fn=8);
            translate([crouton_length-edge_radius, crouton_width-edge_radius, 0]) 
                sphere(r=edge_radius, $fn=8);

            // Top corners
            translate([edge_radius, edge_radius, crouton_height]) 
                sphere(r=edge_radius, $fn=8);
            translate([crouton_length-edge_radius, edge_radius, crouton_height]) 
                sphere(r=edge_radius, $fn=8);
            translate([edge_radius, crouton_width-edge_radius, crouton_height]) 
                sphere(r=edge_radius, $fn=8);
            translate([crouton_length-edge_radius, crouton_width-edge_radius, crouton_height]) 
                sphere(r=edge_radius, $fn=8);
        }
    }
}

// Create bread crust texture on surface
module surface_texture() {
    difference() {
        // Base shape
        crouton_body();

        // Random holes for bread texture
        for (i = [0:30]) {
            translate([
                crouton_length * (0.1 + 0.8 * rands(0, 1, 1)[0]),
                crouton_width * (0.1 + 0.8 * rands(0, 1, 1)[0]),
                crouton_height * (0.1 + 0.8 * rands(0, 1, 1)[0])
            ]) {
                sphere(r=texture_depth * (0.5 + rands(0, 1, 1)[0]), $fn=6);
            }
        }
    }
}

// Add baked surface details
module baked_details() {
    // Darker toasted spots
    for (i = [0:15]) {
        color(darker_color) {
            translate([
                crouton_length * (0.1 + 0.8 * rands(0, 1, 1)[0]),
                crouton_width * (0.1 + 0.8 * rands(0, 1, 1)[0]),
                crouton_height * rands(0.9, 1, 1)[0]
            ]) {
                scale([1.5, 1.5, 0.3]) 
                    sphere(r=texture_depth * 0.8, $fn=8);
            }
        }
    }

    // Crispy edges
    color(darker_color) {
        translate([crouton_length/2, crouton_width/2, crouton_height/2]) {
            rotate([0, 0, 45])
                cube([crouton_length*1.2, crouton_width*0.2, crouton_height*1.2], center=true);
        }
    }
}

// Create irregular top surface for realistic appearance
module irregular_top() {
    difference() {
        surface_texture();

        // Cut irregular top surface
        for (x = [0:5:crouton_length]) {
            for (y = [0:5:crouton_width]) {
                height = crouton_height - 2 * sin(x/3) * cos(y/3);
                translate([x, y, height]) {
                    cube([6, 6, 5], center=true);
                }
            }
        }
    }
}

// Main assembly
