    // Add baked surface details
    baked_details();

    // Add some bread crumbs on surface
    for (i = [0:8]) {
        translate([
            crouton_length * (0.2 + 0.6 * rands(0, 1, 1)[0]),
            crouton_width * (0.2 + 0.6 * rands(0, 1, 1)[0]),
            crouton_height + 0.5
        ]) {
            color(bread_color) {
                sphere(r=0.5 + rands(0, 1, 1)[0], $fn=6);
            }
        }
    }
}

