// Realistic Pretzel - OpenSCAD
// Medium complexity with 3D printable design

// Parameters
pretzel_radius = 8;
loop_radius = 25;
cross_radius = 18;
thickness = 6;
salt_size = 1.2;
salt_density = 0.7;
pretzel_color = "#CD853F";

// Main module
module pretzel() {
    // Create the main pretzel shape
    rotate([0, 0, 0])
    union() {
        // Large loops
        for(i = [0, 1]) {
            rotate([0, 0, i * 180])
            translate([0, loop_radius, 0])
            rotate_extrude(angle = 180, $fn = 32)
            translate([pretzel_radius, 0, 0])
            circle(thickness/2, $fn = 16);
        }

        // Cross piece
        translate([-cross_radius, -cross_radius/2, 0])
        rotate([90, 0, 0])
        linear_extrude(height = cross_radius)
        offset(r = thickness/2)
        difference() {
            square([cross_radius*2, cross_radius], center = false);
            translate([thickness, thickness, 0])
            square([cross_radius*2-thickness*2, cross_radius-thickness], center = false);
        }

        // Bottom loop
        translate([0, -cross_radius - loop_radius/2, 0])
        rotate([90, 0, 0])
        rotate_extrude(angle = 180, $fn = 32)
        translate([loop_radius/2, 0, 0])
        circle(thickness/2, $fn = 16);

        // Connectors
        // Left connector
        translate([-cross_radius, -cross_radius/2, 0])
        rotate([90, 0, 90])
        cylinder(h = cross_radius, r = thickness/2, $fn = 16);

        // Right connector
        translate([cross_radius, -cross_radius/2, 0])
        rotate([90, 0, 90])
    cylinder(h = cross_radius, r = thickness/2, $fn = 16);

    // Bottom connector
    translate([0, -cross_radius - loop_radius/2, 0])
    rotate([0, 0, 0])
    cylinder(h = thickness/2, r1 = thickness/2, r2 = thickness/2, $fn = 16);
    }
}

// Salt crystals module
module salt() {
    color("white")
    for(i = [0:50]) {
        // Random positions on pretzel surface
        angle1 = rands(0, 360, 1)[0];
        angle2 = rands(0, 180, 1)[0];
        pos = rands(-loop_radius, loop_radius, 3);

        if(pos[2] > -thickness && pos[2] < thickness) {
            translate([
                pos[0] + cos(angle1) * pretzel_radius,
                pos[1] + sin(angle1) * pretzel_radius,
                pos[2]
            ])
            scale([1, 1.5, 0.7])
            sphere(salt_size, $fn = 6);
        }
    }
}

// Create the final pretzel
color(pretzel_color)
pretzel();

// Add salt crystals
if(salt_density > 0) salt();

// Realistic Doughnut with Medium Complexity
// Outer dimensions ~75mm diameter, 25mm thick
// 3D printable with 1.2mm wall thickness

