glaze_color = "#CD5C5C";
sprinkle_colors = ["#FF69B4", "#32CD32", "#FFD700", "#4169E1", "#FF4500"];

module doughnut_bread() {
    difference() {
        // Outer torus
        rotate_extrude()
            translate([outer_d/2, 0, 0])
                circle(r=height/2);

        // Inner hole
        rotate_extrude()
            translate([inner_d/2, 0, 0])
                circle(r=height/2 + wall);
    }
}

module glaze_layer() {
    // Slightly smaller than bread for realistic coating
    scale_factor = 0.96;
    difference() {
        scale([scale_factor, scale_factor, 1.1])
            doughnut_bread();

        // Remove bottom for partial glazing
        translate([0, 0, -height/2 - 1])
            cube([outer_d*2, outer_d*2, height/2 + 2], center=true);
    }
}

// Surface texture with small bumps
module surface_texture() {
    for(i = [0:35]) {
        angle = i * 10;
        for(j = [0:8]) {
            y = j * 3 - 12;
            translate([
                cos(angle) * (outer_d/2 + sin(y/height*3.14) * 5),
                sin(angle) * (outer_d/2 + sin(y/height*3.14) * 5),
                y
            ])
                sphere(r=0.8 + sin(i*j)*0.3);
        }
    }
}

// Sprinkles generator
module sprinkles() {
    for(i = [0:45]) {
        angle = i * 137.5;  // Golden angle for distribution
        radius = outer_d/2 + sin(i*0.35) * 8;

        // Random height on top surface
        z_pos = sin(angle/10) * 3 + 8;

        translate([
            cos(angle) * radius,
            sin(angle) * radius,
            z_pos
        ]) {
            rotate([90, 0, angle*2])
                color(sprinkle_colors[i % len(sprinkle_colors)])
                    scale([1, 0.4, 0.4])
                        cylinder(h=4, r=0.8);
        }
    }
}

// Assembly
union() {
    // Bread base with texture
    color(bread_color)
        union() {
            doughnut_bread();
            surface_texture();
        }

    // Glaze layer
    color(glaze_color)
        glaze_layer();

    // Sprinkles on top
    sprinkles();
}

