// Realistic 3D Printable Roll (Bread Roll)
// Approx. 70mm diameter, 45mm height

// Main roll body
module roll_body() {
    color("BurlyWood")
    difference() {
        // Main dome shape
        scale([1, 1, 0.7]) 
            sphere(d=70, $fn=100);

        // Flat bottom
        translate([0, 0, -35])
            cube([80, 80, 70], center=true);
    }
}

// Top surface texture - small bumps
module roll_texture() {
    for(i = [0:30]) {
        rotate([0, 0, i*12])
        translate([random(5, 25), random(5, 25), 0])
        translate([0, 0, 25])
        color("Wheat")
        scale([1, 1, 0.3])
        sphere(d=random(2,4), $fn=20);
    }
}

// Side texture - diagonal lines
module side_lines() {
    color("Tan")
    for(angle = [0:20:340]) {
        rotate([0, 0, angle])
        translate([32, 0, 10])
        rotate([90, 0, 45])
        cylinder(h=30, d1=0.5, d2=1.5, $fn=3);
    }
}

// Bottom seam
module bottom_seam() {
    color("SandyBrown")
    rotate_extrude($fn=100)
    translate([33, 0, 0])
    circle(d=4);
}

// Sesame seeds on top
module sesame_seeds() {
    color("FloralWhite")
    for(i = [0:40]) {
        theta = i * 137.508; // Golden angle
        r = sqrt(i) * 3.5;
        x = r * cos(theta);
        y = r * sin(theta);
        z = 25 - sqrt(x*x + y*y) * 0.1;

        translate([x, y, z])
        scale([0.8, 0.5, 0.3])
        sphere(d=2, $fn=6);
    }
}

// Combine all parts
union() {
    // Main roll
    roll_body();

    // Surface details
    roll_texture();
    side_lines();

    // Top decorations
    translate([0, 0, 0])
    sesame_seeds();

    // Bottom detail
    translate([0, 0, -5])
    bottom_seam();
}

