// Realistic Toast Model
// Medium complexity - includes texture, crust, and toast characteristics

// Main parameters
bread_width = 80;
bread_height = 70;
bread_thickness = 12;
crust_thickness = 2;
seed_count = 12;
texture_depth = 0.5;

// Colors
crust_color = [139/255, 69/255, 19/255];
bread_color = [245/255, 222/255, 179/255];
seed_color = [160/255, 82/255, 45/255];
toast_color = [210/255, 180/255, 140/255];

// Main toast body
module toast_body() {
    difference() {
        // Main bread shape
        translate([0, 0, bread_thickness/2])
            color(toast_color)
            cube([bread_width, bread_height, bread_thickness], center=true);

        // Rounded corners
        for(i = [-1, 1]) {
            for(j = [-1, 1]) {
                translate([i * (bread_width/2 - 10), j * (bread_height/2 - 10), 0])
                    rotate([0, 0, 45])
                    cube([20, 20, bread_thickness + 2], center=true);
            }
        }
    }
}

// Crust layer
module crust() {
    difference() {
        // Outer crust
        translate([0, 0, bread_thickness/2])
            color(crust_color)
            cube([bread_width + 1, bread_height + 1, bread_thickness + 0.5], center=true);

        // Inner cutout
        translate([0, 0, bread_thickness/2])
            cube([bread_width - 2, bread_height - 2, bread_thickness + 1], center=true);
    }
}

// Bread texture pattern
module toast_texture() {
    difference() {
        union() {
            // Horizontal lines
            for(i = [-3:3]) {
                translate([0, i * 8, bread_thickness - texture_depth])
                    color(bread_color)
                    cube([bread_width - 10, 1, texture_depth], center=true);
            }

            // Vertical lines
            for(i = [-3:3]) {
                translate([i * 8, 0, bread_thickness - texture_depth])
                    color(bread_color)
                    cube([1, bread_height - 10, texture_depth], center=true);
            }
        }

        // Smooth out edges
        translate([0, 0, bread_thickness/2])
            cube([bread_width - 4, bread_height - 4, bread_thickness + 1], center=true);
    }
}

// Sesame seeds
module sesame_seeds() {
    for(i = [1:seed_count]) {
        angle = i * 137.5; // Golden angle for natural distribution
        radius = 15 + (i * 3) % 20;
        x_pos = cos(angle) * radius;
        y_pos = sin(angle) * radius;

        if(abs(x_pos) < bread_width/2 - 5 && abs(y_pos) < bread_height/2 - 5) {
            translate([x_pos, y_pos, bread_thickness + 0.3])
                color(seed_color)
                scale([1, 1.5, 0.5])
                sphere(1.2);
        }
    }
}

// Burnt spots for realism
module burnt_spots() {
    for(i = [1:8]) {
        angle = i * 45;
        radius = 20 + (i * 5) % 15;
        x_pos = cos(angle) * radius;
        y_pos = sin(angle) * radius;

        translate([x_pos, y_pos, bread_thickness + 0.2])
            color([0.2, 0.1, 0.05, 0.7])
            scale([1, 1, 0.1])
            sphere(2 + (i % 3));
    }
}

// Assemble the toast
union() {
    // Base structure
    crust();
    toast_body();

    // Surface details
    translate([0, 0, 0.5])
        toast_texture();

    // Decorative elements
    sesame_seeds();
    burnt_spots();
}

