// food color
color([0.85,0.65,0.45])

module crumb() {
    // main porous body
    difference() {
        hull() {
            // create an irregular blob with spheres
            translate([0,0, 5]) sphere(r=18,$fn=32);      // top
            translate([-15,-8,-10]) sphere(r=12,$fn=32);  // front-left
            translate([12,10,-8]) sphere(r=13,$fn=32);    // back-right
            translate([-5,15,-5]) sphere(r=10,$fn=32);    // back-left
        }

        // internal bubbles / voids
        for (i=[0:30]) {
            translate([rands(-15,15,1)[0],
                       rands(-15,15,1)[0],
                       rands(-12,12,1)[0]])
            sphere(r=rands(2,5,1)[0],$fn=16);
        }
