    // thin crust layer on top surface
    intersection() {
        translate([0,0,15]) cube([40,40,2],center=true);
        hull() {
            sphere(r=18,$fn=32);
        }
    }

    // scattered micro-bumps for texture
    for (j=[0:50]) {
        translate([rands(-18,18,1)[0],
                   rands(-18,18,1)[0],
                   rands(-12,8,1)[0]])
        scale([1,1,0.4])
        sphere(r=rands(0.6,1.2,1)[0],$fn=8);
    }
}

