// Realistic Crust Food Item - Medium Complexity
// A golden-brown pie crust with flaky layers and crimped edges

crust_color = "#D4A574";
edge_color = "#C19660";

// Main pie crust base with slight dome shape
module crust_base() {
    color(crust_color) 
    difference() {
        // Main dome shape
        scale([1, 1, 0.3]) 
        sphere(d = 80, $fn = 64);

        // Hollow out the center
        translate([0, 0, -2]) 
        scale([0.9, 0.9, 0.4]) 
        sphere(d = 80, $fn = 64);

        // Flatten bottom
        translate([0, 0, -12]) 
        cube([100, 100, 25], center = true);
    }
}

// Crimped edge details
module crimped_edge() {
    color(edge_color) 
    for(i = [0:15]) {
        rotate([0, 0, i * 22.5]) 
        translate([40, 0, 0]) 
        union() {
            // Finger press indents
            scale([0.8, 1.5, 0.5]) 
            rotate([0, 90, 0]) 
            cylinder(h = 8, r1 = 3, r2 = 2, $fn = 16);

            // Ridge between indents
            translate([0, 0, 2]) 
            scale([1.2, 0.5, 0.3]) 
            cylinder(h = 5, r = 2, $fn = 16);
        }
    }
}

// Flaky layers texture on surface
module flaky_texture() {
    color(crust_color) 
    for(i = [0:12]) {
        rotate([0, 0, i * 30]) 
        translate([0, 0, -5]) 
        for(j = [0:5]) {
            translate([j * 5 + 5, 0, j * 0.5]) 
            rotate([0, 0, j * 15]) 
            scale([1, 0.3, 0.1]) 
            sphere(d = 8, $fn = 8);
        }
    }
}

// Docking holes (small holes to prevent bubbling)
