// Realistic crisp (potato chip) with medium complexity
// Features: curved shape, surface bubbles, salt grains, appropriate coloring

module salt_grain(pos) {
    translate(pos) scale([1,1,0.7]) sphere(0.4);
}

module surface_bubble(pos, size) {
    translate(pos) sphere(size);
}

module crisp_body() {
    color([0.85, 0.6, 0.3]) {
        // Main chip body with gentle curvature
        difference() {
            // Basic curved shape
            translate([0,0,0]) 
            scale([1, 0.7, 0.15]) 
            rotate([0,15,0])
            sphere(30);

            // Trim to chip shape
            intersection() {
                cube([80,80,20], center=true);
                translate([0,0,-10]) cube([80,80,20], center=true);
            }
        }

        // Add subtle thickness variation
        difference() {
            scale([1, 0.7, 0.12]) 
            rotate([0,15,0])
            sphere(30);

            // Create uneven edges
            for(i = [0:10:350]) {
                rotate([0,0,i]) 
                translate([28+sin(i*5)*3, 0, -5]) 
                scale([1,1,3]) 
                cylinder(h=10, r1=3, r2=0);
            }
        }
    }
}

module bubbles() {
    color([0.9, 0.65, 0.35]) {
        // Random surface bubbles typical of fried crisps
        for(i = [0:20]) {
            theta = rands(0,360,1)[0];
            phi = rands(0,180,1)[0];
            r = 28 + rands(-2,2,1)[0];
            x = r * cos(theta) * sin(phi);
            y = r * sin(theta) * sin(phi) * 0.7;
            z = r * cos(phi) * 0.15 - 2;
            surface_bubble([x,y,z], rands(0.5,2,1)[0]);
        }
    }
}

module salt() {
    color([0.95,0.95,0.95]) {
        // Salt crystals distributed across surface
        for(i = [0:50]) {
            theta = rands(0,360,1)[0];
            phi = rands(30,150,1)[0];
            r = 25 + rands(-3,3,1)[0];
            x = r * cos(theta) * sin(phi);
            y = r * sin(theta) * sin(phi) * 0.7;
            z = r * cos(phi) * 0.15;
            salt_grain([x,y,z]);
        }
    }
}

// Main assembly
union() {
    crisp_body();
    bubbles();
    salt();
}

// Add slight overall curvature
rotate([5,0,0]) 
rotate([0,8,0]) 
translate([0,0,5]) 
union() {
    crisp_body();
