flake_width = 40;
flake_thickness = 2.5;

// main flake body
module flake_body() {
    // thin base sheet with gentle bend
    translate([0, 0, flake_thickness/2])
    scale([1, 0.8, 1])
    rotate([5, 0, 0])
    cube([flake_length, flake_width, flake_thickness], center = true);
}

// raised ridge along one side
module ridge() {
    for (i = [-25:5:25]) {
        translate([i, -15, 0])
        rotate([0, 10, 0])
        scale([0.8, 0.3, 0.8])
        cylinder(h = 1.2, r = 3, center = true);
    }
