    for (i = [-25:8:25]) {
        for (j = [-15:6:15]) {
            if (abs(i) + abs(j) > 10) {
                translate([i, j, 1.5])
                sphere(r = 0.8);
            }
        }
    }
}

// subtle curl at the tip
