// Dextrin food model - realistic style with medium complexity
// Features a cluster of dextrin granules with characteristic texture

$fn = 32;

// Main parameters
granule_size = 4;
cluster_size = 60;
granules_count = 40;
wall_thickness = 1.2;

// Color definitions
dextrin_color = "#D4A574";
light_dextrin = "#E6C68A";
dark_dextrin = "#B8955A";

// Random seed for consistent texture
seed = 42;

// Module to create a single dextrin granule
module dextrin_granule(size) {
    // Main granule body
    color(dextrin_color) {
        sphere(r = size);

        // Add surface texture with small bumps
        for (i = [0:7]) {
            rotate([rand(i*3)*360, rand(i*5)*360, rand(i*7)*360])
            translate([rand(i*11)*size*0.7, rand(i*13)*size*0.7, rand(i*17)*size*0.7])
            color(light_dextrin)
            sphere(r = size * 0.15);
        }
    }
}

// Module for the base cluster
module base_cluster() {
    difference() {
        // Main base shape
        color(dark_dextrin) {
            sphere(r = cluster_size/2);
        }

        // Hollow out the center for printing
        sphere(r = cluster_size/2 - wall_thickness);
    }
}

// Helper function for pseudo-random values
function rand(n) = (n * 9301 + 49297) % 233280 / 233280;

// Create the dextrin cluster
module dextrin_cluster() {
    // Base cluster
    base_cluster();

    // Add granules on the surface
    for (i = [0:granules_count-1]) {
        // Calculate position on sphere surface using fibonacci distribution
        phi = acos(1 - 2*(i+0.5)/granules_count);
        theta = 3.14159 * (1 + sqrt(5)) * (i+0.5);

        // Randomize position slightly
        offset = [rand(i*97)*20-10, rand(i*101)*20-10, rand(i*103)*20-10];

        translate([
            (cluster_size/2 - 2) * cos(theta) * sin(phi) + offset[0],
            (cluster_size/2 - 2) * sin(theta) * sin(phi) + offset[1],
            (cluster_size/2 - 2) * cos(phi) + offset[2]
        ]) {
            // Random rotation for variation
            rotate([rand(i*107)*360, rand(i*109)*360, rand(i*113)*360]) {
                // Vary granule size
                actual_size = granule_size * (0.7 + rand(i*127)*0.6);
                dextrin_granule(actual_size);
            }
        }
    }

    // Add some connecting bridges between granules for printability
    for (i = [0:granules_count/4-1]) {
        idx1 = i*4;
        idx2 = (i*4 + 2) % granules_count;

        // Calculate positions
        phi1 = acos(1 - 2*(idx1+0.5)/granules_count);
        theta1 = 3.14159 * (1 + sqrt(5)) * (idx1+0.5);
        pos1 = [
            (cluster_size/2 - 2) * cos(theta1) * sin(phi1),
            (cluster_size/2 - 2) * sin(theta1) * sin(phi1),
            (cluster_size/2 - 2) * cos(phi1)
        ];

        phi2 = acos(1 - 2*(idx2+0.5)/granules_count);
        theta2 = 3.14159 * (1 + sqrt(5)) * (idx2+0.5);
        pos2 = [
            (cluster_size/2 - 2) * cos(theta2) * sin(phi2),
            (cluster_size/2 - 2) * sin(theta2) * sin(phi2),
            (cluster_size/2 - 2) * cos(phi2)
        ];

        // Create connecting cylinder
        color(dextrin_color) {
            translate(pos1) {
                rotate([0, 0, atan2(pos2[1]-pos1[1], pos2[0]-pos1[0])]) {
                    rotate([0, acos((pos2[2]-pos1[2])/norm(pos2-pos1)), 0]) {
                        cylinder(h = norm(pos2-pos1), r = wall_thickness/2, $fn = 8);
                    }
                }
            }
        }
    }
}

// Add a flat base for stability
module print_base() {
    color(dark_dextrin) {
        cylinder(h = 3, r = cluster_size/2);
    }
}

// Assemble the final model
translate([0, 0, 3])
dextrin_cluster();

print_base();

