                        scale([0.1, 0.05, 1.2]) 
                            sphere(r = groats_radius * 0.2);
            }
        }
    }
}

module groats_pile() {
    // Create a natural-looking pile of groats
    color(groats_color) {
        union() {
            // Randomly placed and rotated groats
            for(i = [0:groats_count-1]) {
                translate([
                    (i % 4 - 1.5) * 8 + (i % 3) * 3,
                    (floor(i/4) - 1) * 8 + (i % 2) * 2,
                    i * 1.5 + (i % 5) * 0.5
                ]) {
                    rotate([rand(i*2)*15, rand(i*3+1)*20, rand(i*5+2)*360]) {
                        groat_single();
                    }
                }
            }
        }
    }
}

