    for (row = [0:4]) {
        for (col = [0:6]) {
            translate([
                col * 6 + 3, 
                (row - 2) * 2.5, 
                sin(col * 30) * 1.5
            ])
            rotate([0, 90, 0])
                translate([0, 0, -kernel_height/2])
                cylinder(h=kernel_height/3, d1=2.5, d2=1.5, $fn=6);
