module berry_body() {
    // Main drupelet cluster shape â€“ slightly elongated dome
    hull() {
        for (i = [0:11]) {
            rotate([0, 0, i * 30])
            translate([0, 0, 0])
            sphere(d = 42); // Base spheres
        }
    }

    // Top tapering dome
    translate([0, 0, 35])
    scale([0.8, 0.8, 0.6])
    sphere(d = 30);
}

module drupelets() {
    // Generate individual drupelets over the berry surface
    for (ring = [0:3]) {
        h = 10 + ring * 10; // Height of each ring
        r = 20 - ring * 4;  // Radius at that height
        n = 12 - ring * 2;  // Fewer drupelets toward top

        for (i = [0:n-1]) {
            angle = i * 360 / n;
            translate([
                r * cos(angle),
                r * sin(angle),
                h
            ])
            rotate([0, 0, angle])
            scale([1, 1, 1.2])
            sphere(d = 6.5); // Each drupelet ~6.5 mm
        }
    }

    // Crown drupelet at very top
    translate([0, 0, 48])
    sphere(d = 5);
}

module seeds() {
    // Tiny seeds inside some drupelets
    seed_d = 1.8;
    for (ring = [0:2]) {
        h = 10 + ring * 12;
        r = 18 - ring * 4;
        n = 10 - ring * 2;

        for (i = [0:n-1]) {
            angle = i * 360 / n + ring * 5; // Stagger rings
            translate([
                r * cos(angle),
                r * sin(angle),
                h + 2
            ])
            color("saddlebrown")
            sphere(d = seed_d);
        }
    }
}

