    // Green calyx (leafy top) with 5 lobes
    color("forestgreen")
    union() {
        // Central disk
        cylinder(h = 4, d = 15, center = true);

        // Five lobes
        for (i = [0:4]) {
            rotate([0, 0, i * 72])
            translate([7, 0, 0])
            rotate([0, 90, 0])
            cylinder(h = 12, d1 = 6, d2 = 2);
        }
    }
}

// Main assembly
