        translate([s.x-r, r, r]) sphere(r);
        translate([r, s.y-r, r]) sphere(r);
        translate([s.x-r, s.y-r, r]) sphere(r);
        translate([r, r, s.z-r]) sphere(r);
        translate([s.x-r, r, s.z-r]) sphere(r);
        translate([r, s.y-r, s.z-r]) sphere(r);
        translate([s.x-r, s.y-r, s.z-r]) sphere(r);
    }
}

