        hull() {
            translate([0, 0, 0]) 
                scale([1, 0.3, thickness]) 
                    sphere(d=width);
            translate([length*0.7, 0, 0]) 
                scale([0.7, 0.2, thickness]) 
                    sphere(d=width*0.8);
            translate([length, 0, 0]) 
                scale([0.3, 0.1, thickness]) 
                    sphere(d=width*0.4);
        }

        // Add natural leaf tapering
        for(i = [0:10]) {
            translate([i*length/10, 0, thickness*2])
                rotate([0, 90, 0])
                    cylinder(h=width*0.4, d1=width*(1-i/15), d2=0);
        }
    }
}

module corn_kernels(rows, cols) {
    color("#F5DEB3")  // Corn kernel color
    for(r = [0:rows-1]) {
        for(c = [0:cols-1]) {
