// Chaff (wheat bran) - realistic style, medium complexity
// Dimensions: ~30x20x8mm individual pieces

// Color definitions
color_chaff = "#C19A6B";
color_dark = "#8B4513";
color_light = "#D2B48C";

// Main chaff module
module chaff_piece(x_offset, y_offset, rotation, scale_factor) {
    translate([x_offset, y_offset, 0])
    rotate([0, 0, rotation])
    scale([scale_factor, scale_factor, scale_factor]) {

        // Base flake body
        difference() {
            // Main chaff body - irregular oval shape
            scale([1.5, 1, 0.3])
            sphere(r=10, $fn=20);

            // Create irregular edges through subtraction
            for(i = [0:3]) {
                rotate([0, 0, i*90 + 15])
                translate([12, 0, -2])
                scale([1, 0.7, 1])
                cylinder(h=10, r1=3, r2=0, $fn=6);
            }

            // Add central groove
            rotate([90, 0, 0])
            translate([0, 0, -1])
            cylinder(h=20, r1=1, r2=0.5, $fn=8);
        }

        // Surface texture - small bumps for bran texture
        for(i = [1:15]) {
            theta = i * 137.5;
            r = 5 + sin(i*3)*3;
            translate([r*cos(theta), r*sin(theta), 3])
            scale([1, 1, 0.5])
            sphere(r=0.5 + 0.3*cos(i*2), $fn=6);
        }

        // Edge detail - thin flakes
        for(i = [0:8]) {
            angle = i * 45 + 5;
            distance = 9 + 2*sin(i*2);
            rotate([0, 0, angle])
            translate([distance, 0, 1.5])
            rotate([0, 0, -20 + i*3])
            scale([0.3, 2, 0.1])
            cube([1, 1, 1]);
        }

        // Central vein
        color(color_dark)
        difference() {
            rotate([90, 0, 0])
            translate([0, 0, 2.8])
            scale([0.1, 1.2, 0.05])
            cylinder(h=15, r=1, $fn=8);


            // Add small holes along vein
            for(j = [0:4]) {
                translate([0, -7 + j*3.5, -0.1])
                sphere(r=0.3, $fn=5);
            }
        }
    }
}

